protected String issueCommandToServer(String buffer, ChangeCapsule changeCapsule) throws IOException {
    URLConnection userConnection = serverURL.openConnection();
    userConnection.setDoInput(true);
    userConnection.setDoOutput(true);
    userConnection.setRequestProperty("Content-Type", "application/x-www-form-urlencoded");
    OutputStreamWriter nw = new OutputStreamWriter(userConnection.getOutputStream());
    String server = ApplyChangesServlet.PARAMETER_COMMAND + "=" + buffer;
    server += "&" + ApplyChangesServlet.PARAMETER_CAPSULE + "=" + URLEncoder.encode(changeCapsule.toJSON(), "UTF-8");
    nw.write(server);
    nw.flush();
    BufferedReader xml = new BufferedReader(new InputStreamReader(userConnection.getInputStream()));
    StringBuffer response = new StringBuffer();
    String str;
    while (null != ((str = xml.readLine()))) {
        response.append(str);
    }
    nw.close();
    xml.close();
    return response.toString();
}

public Vector<String> getNetworkServersIPs(String netaddress) {
    Vector<String> result = new Vector<String>();
    boolean serverline = false;
    String line;
    String[] splitline;
    try {
        URL url = new URL(netaddress);
        URLConnection connection = url.openConnection();
        BufferedReader reader = new BufferedReader(new InputStreamReader(connection.getInputStream()));
        while ((line = reader.readLine()) != null) {
            if ((serverline) && line.startsWith(";")) {
                serverline = false;
            }
            if (serverline) {
                splitline = line.split(":");
                result.add(splitline[1]);
            }
            if (line.startsWith("!SERVERS")) {
                serverline = true;
            }
        }
    } catch (MalformedURLException e) {
        e.printStackTrace();
    } catch (IOException e) {
        e.printStackTrace();
    }
    return result;
}

    public void  doGET(HttpServletRequest url, HttpServletResponse reason) throws ServletException, IOException {
        String lectedLine = url.getParameter("SelectedPage");
        Page account = null;
        PortalRequest portalRequest = PortalRequest.getCurrentRequest();
        if (lectedLine == null) {
            try {
                Property PageProperty = Property.getProperty("HomePage");
                lectedLine = PageProperty.getValue();
            } catch (PersistentModelException ea) {
                myLogger.info("Page could not be found due to an exception");
                reason.sendError(reason.SC_NOT_FOUND);
                return;
            }
        }
        myLogger.info("Handling Page Request for Page " + lectedLine);
        try {
            if (account == null) {
                try {
                    account = Page.getPage(Long.parseLong(lectedLine));
                } catch (NumberFormatException ea) {
                    List<Page> PossibilityLine = Page.findByName(lectedLine);
                    if (PossibilityLine.size() > 0) {
                        account = PossibilityLine.get(0);
                    } else {
                        reason.sendError(reason.SC_NOT_FOUND);
                        return;
                    }
                }
            }
            if (!account.isVisibleTo(portalRequest.getCurrentUser())) {
                Property PageProperty = Property.getProperty("HomePage");
                lectedLine = PageProperty.getValue();
                account = Page.getPage(Long.parseLong(lectedLine));
            }
            try {
                Property property = Property.getProperty("LogPageRequests");
                if (property.getValue().toLowerCase().equals("true")) {
                    String referer = url.getHeader("Referer");
                    if (referer == null || referer.indexOf(portalRequest.getRequest().getServerName()) > 0) {
                        referer = " ";
                    } else {
                        if (referer.length() >= 200) {
                            referer = referer.substring(0, 198);
                        }
                    }
                    PageRequest.createRequest(portalRequest.getCurrentHREF(), account, portalRequest.getCurrentUser(), portalRequest.getRequest().getRemoteAddr(), referer);
                }
            } catch (PersistentModelException ea) {
                myLogger.log(Level.WARNING, "Log Page Requests property was not found.", ea);
            }
            if (account.isVisibleTo(portalRequest.getCurrentUser())) {
                HttpSession session = url.getSession();
                if (session != null) {
                    RecentlyViewedManager rvm = (RecentlyViewedManager) session.getAttribute("ipoint.RecentlyViewedManager");
                    if (rvm == null) {
                        rvm = new RecentlyViewedManager();
                    }
                    rvm.add(account);
                    session.setAttribute("ipoint.RecentlyViewedManager", rvm);
                }
                PageCacheEntry entry = null;
                if (!portalRequest.isPost() && !account.isEditableBy(portalRequest.getCurrentUser())) {
                    try {
                        int pageCacheTime = 60000;
                        try {
                            Property pageCacheTimeProperty = Property.getProperty("PageCacheTime");
                            pageCacheTime = Integer.parseInt(pageCacheTimeProperty.getValue());
                            pageCacheTime *= 1000;
                        } catch (PersistentModelException pme) {
                            myLogger.warning("Ignoring Exception when retrieving PageCacheTime property");
                        } catch (NumberFormatException nfe) {
                        }
                        entry = PageCacheEntry.find(portalRequest.getCurrentHREF(), account, portalRequest.getCurrentUser(), pageCacheTime);
                    } catch (PersistentModelException ea) {
                        myLogger.log(Level.WARNING, "An error occurred finding a page cache entry", ea);
                    }
                }
                reason.setContentType("text/html");
                reason.setHeader("Cache-Control", "no-cache");
                boolean processed = false;
                if (entry != null) {
                    String tempFile = entry.getTempFile();
                    File file = new File(tempFile);
                    if (file.exists() && file.isFile() && file.canRead()) {
                        FileReader reader = new FileReader(file);
                        BufferedReader br = new BufferedReader(reader);
                        PrintWriter out = reason.getWriter();
                        out.write("<!-- Starting to output cached page in " + portalRequest.elapsedTime() + " ms -->
");
                        while (br.ready()) {
                            out.write(br.readLine() + "
");
                        }
                        out.write("<!-- Cached output produced in " + portalRequest.elapsedTime() + " ms -->
");
                        reader.close();
                        processed = true;
                        portalRequest.getMBeans().incrementStatistics(ManagementMBeans.StatisticsType.PageTotal, account.getName() + "-" + account.getID(), portalRequest.elapsedTime(), true);
                    } else {
                        entry.delete();
                    }
                }
                if (!processed) {
                    PrintWriter out = reason.getWriter();
                    out.write("<!-- Output produced by iPoint Portal -->
");
                    PageRenderer renderer = new PageRenderer(account);
                    long startTime = System.currentTimeMillis();
                    url.setAttribute(ELConstants.IPOINT_USER, portalRequest.getCurrentUser());
                    url.setAttribute(ELConstants.IPOINT_PAGE, account);
                    url.setAttribute(ELConstants.IPOINT_TEMPLATE, account.getTemplate());
                    url.setAttribute(ELConstants.IPOINT_PORTAL_PROPERTIES, Property.getPropertiesMap());
                    url.setAttribute(ELConstants.IPOINT_USER_PROPERTIES, portalRequest.getCurrentUser().getProperties());
                    renderer.preProcess();
                    portalRequest.getMBeans().incrementStatistics(ManagementMBeans.StatisticsType.PagePreProcess, account.getName() + "-" + account.getID(), System.currentTimeMillis() - startTime, false);
                    long renderStart = System.currentTimeMillis();
                    renderer.render();
                    portalRequest.getMBeans().incrementStatistics(ManagementMBeans.StatisticsType.PageRender, account.getName() + "-" + account.getID(), System.currentTimeMillis() - renderStart, false);
                    portalRequest.getMBeans().incrementStatistics(ManagementMBeans.StatisticsType.PageTotal, account.getName() + "-" + account.getID(), System.currentTimeMillis() - startTime, false);
                    out.write("<!-- output produced in " + PortalRequest.getCurrentRequest().elapsedTime() + " ms -->
");
                    if (!account.isEditableBy(portalRequest.getCurrentUser())) {
                        if (reason instanceof CachingResponseWrapper) {
                            CachingResponseWrapper crw = (CachingResponseWrapper) reason;
                            crw.flushBuffer();
                            Property tempProperty = Property.getProperty("UploadLocation");
                            File tempDirectory = new File(tempProperty.getValue());
                            if (tempDirectory.exists() && tempDirectory.canWrite()) {
                                File file = File.createTempFile("iPointPage", ".html", tempDirectory);
                                try {
                                    FileWriter fw = new FileWriter(file);
                                    fw.write(crw.getOutput());
                                    fw.flush();
                                    fw.close();
                                    PageCacheEntry.create(portalRequest.getCurrentHREF(), account, portalRequest.getCurrentUser(), file.getAbsolutePath());
                                } catch (IOException ea) {
                                }
                            } else {
                                myLogger.warning("Can not write to directory " + tempProperty.getValue());
                            }
                        }
                    }
                }
            } else {
                myLogger.info("Current User is Forbidden from seeing this Page");
                reason.sendError(reason.SC_FORBIDDEN);
            }
        } catch (PersistentModelException ea) {
            myLogger.log(Level.INFO, "Unable to find page " + lectedLine, ea);
            reason.sendError(reason.SC_NOT_FOUND);
        } catch (PresentationException pe) {
            pe.printStackTrace();
            throw new ServletException("Error processing the page", pe);
        }
    }

