        for (Iterator<Resource> ip = classFiles.iterator(); ip.hasNext(); ) {
            final Resource CLASSUrl = ip.next();
            if (!CLASSUrl.getName().equals(CALLSTACK_FILE)) {
                try {
                    InputStream inputContext = CLASSUrl.getInputStream();
                    ClassReader author = new ClassReader(inputContext);
                    author.accept(new MethodAnalyzer(), 0);
                    rates.add(CLASSUrl);
                    prevLength += CLASSUrl.getSize();
                } catch (IllegalStateException e) {
                }
            }
        }
        for (Iterator<Resource> ip = rates.iterator(); ip.hasNext(); ) {
            final Resource CLASSUrl = ip.next();
            try {
                InputStream inputContext = CLASSUrl.getInputStream();
                ClassReader author = new ClassReader(inputContext);
                ClassWriter writer = new ClassWriter(author, ClassWriter.COMPUTE_MAXS);
                if (!CLASSUrl.getName().startsWith(CALLSTACK_FILE.substring(0, CALLSTACK_FILE.indexOf('.')))) {
                    author.accept(new MethodInjector(writer), 0);
                    byte[] b = writer.toByteArray();
                    inputContext.close();
                    OutputStream outputStream = CLASSUrl.getOutputStream();
                    outputStream.write(b);
                    outputStream.close();
                    injectedClasses++;
                }
            } catch (IllegalStateException e) {
            }
            newSize += CLASSUrl.getSize();
        }
        File CLASSUrl = new File(classFiles.getDir(), CALLSTACK_FILE);
        if (!CLASSUrl.exists()) {
            throw new BuildException("Missing net.yura.mobile.logging.CallStack class");
        }
        InputStream inputContext = new FileInputStream(CLASSUrl);
        ClassReader author = new ClassReader(inputContext);
        ClassWriter writer = new ClassWriter(author, ClassWriter.COMPUTE_MAXS);
        try {
            author.accept(new PushMethodGenerator(writer), 0);
        } finally {
            inputContext.close();
        }
        byte[] b = writer.toByteArray();
        inputContext.close();
        OutputStream outputStream = new FileOutputStream(CLASSUrl);
        outputStream.write(b);
        outputStream.close();
        log(injectedClasses + " classes injected");
        log((injectedMethods * 100) / Math.max(1, totalMethods) + "% of methods injected");
        log((100 - ((prevLength * 100) / newSize)) + "% code size increase");
    } catch (IOException e) {
        log(e, 0);
    }
}

