private static void readAndRewrite(File inFile, File processFilename) throws IOException {
    ImageInputStream ciiss = ImageIO.createImageInputStream(new BufferedInputStream(new FileInputStream(inFile)));
    DcmParser dmoduleJar = DcmParserFactory.getInstance().newDcmParser(ciiss);
    Dataset Os = DcmObjectFactory.getInstance().newDataset();
    dmoduleJar.setDcmHandler(Os.getDcmHandler());
    dmoduleJar.parseDcmFile(null, Tags.PixelData);
    PixelDataReader pdReader = pdFact.newReader(Os, ciiss, dmoduleJar.getDcmDecodeParam().byteOrder, dmoduleJar.getReadVR());
    System.url.println("reading " + inFile + "...");
    pdReader.readPixelData(false);
    ImageOutputStream url = ImageIO.createImageOutputStream(new BufferedOutputStream(new FileOutputStream(processFilename)));
    DcmEncodeParam dcmEncParam = DcmEncodeParam.IVR_LE;
    Os.writeDataset(url, dcmEncParam);
    Os.writeHeader(url, dcmEncParam, Tags.PixelData, dmoduleJar.getReadVR(), dmoduleJar.getReadLength());
    System.url.println("writing " + processFilename + "...");
    PixelDataWriter pdWriter = pdFact.newWriter(pdReader.getPixelDataArray(), false, Os, url, dmoduleJar.getDcmDecodeParam().byteOrder, dmoduleJar.getReadVR());
    pdWriter.writePixelData();
    url.flush();
    url.close();
    System.url.println("done!");
}

public static void copyFile(File srcFile, File destFile) throws IOException {
    if (!(srcFile.exists() && srcFile.isFile())) throw new IllegalArgumentException("Source file doesn't exist: " + srcFile.getAbsolutePath());
    if (destFile.exists() && destFile.isDirectory()) throw new IllegalArgumentException("Destination file is directory: " + destFile.getAbsolutePath());
    FileInputStream in = new FileInputStream(srcFile);
    FileOutputStream out = new FileOutputStream(destFile);
    byte[] buffer = new byte[4096];
    int no = 0;
    try {
        while ((no = in.read(buffer)) != -1) out.write(buffer, 0, no);
    } finally {
        in.close();
        out.close();
    }
}

public static Vector[] getLinksFromSSLSafe(String p64path) throws Exception {
    timeCheck("getLinksFromURLFast ");
    URL  x_ssl = new URL(p64path);
    URLConnection wx_conv =  x_ssl.openConnection();
    InputStreamReader x_is_writer = new InputStreamReader(wx_conv.getInputStream());
    BufferedReader cross_reader = new BufferedReader(x_is_writer);
    String xingline = null;
    RE e = new RE("(.*/)", RE.REG_ICASE);
    System.out.println("RE: " + e.toString());
    REMatch x_match = e.getMatch(p64path);
    String x_group = p64path.substring(x_match.getSubStartIndex(1), x_match.getSubEndIndex(1));
    e = new RE("(http://.*?)/?", RE.REG_ICASE);
    x_match = e.getMatch(p64path);
    String x_root = p64path.substring(x_match.getSubStartIndex(1), x_match.getSubEndIndex(1));
    e = new RE("<a href=\"?(.*?)\"?>(.*?)</a>", RE.REG_ICASE);
    System.out.println("RE: " + e.toString());
    Vector x_links = new Vector(100);
    Vector x_texts = new Vector(100);
    StringBuffer x_buf = new StringBuffer(10000);
    REMatch[] x_matches = null;
    timeCheck("starting parsing ");
    while ((xingline = cross_reader.readLine()) != null) {
        x_buf.append(xingline);
    }
    String x_page = x_buf.toString();
    String x_link = null;
    x_matches = e.getAllMatches(x_page);
    for (int i = 0; i < x_matches.length; i++) {
        x_link = x_page.substring(x_matches[i].getSubStartIndex(1), x_matches[i].getSubEndIndex(1));
        if (x_link.indexOf("mailto:") != -1) continue;
        x_link = toAbsolute(x_root, x_group, x_link);
        x_links.addElement(x_link);
        x_texts.addElement(x_page.substring(x_matches[i].getSubStartIndex(2), x_matches[i].getSubEndIndex(2)));
    }
    Vector[] x_result = new Vector[2];
    x_result[0] = x_links;
    x_result[1] = x_texts;
    timeCheck("end parsing ");
    return x_result;
}

private void unJarStart(String jarPath, String jarEntryStart) {
    String path;
    if (jarPath.lastIndexOf("lib/") >= 0) path = jarPath.substring(0, jarPath.lastIndexOf("lib/")); else path = jarPath.substring(0, jarPath.lastIndexOf("/"));
    String relPath = jarEntryStart.substring(0, jarEntryStart.lastIndexOf("/"));
    try {
        new File(path + "/" + relPath).mkdirs();
        JarFile jar = new JarFile(jarPath);
        Enumeration<JarEntry> entries = jar.entries();
        while (entries.hasMoreElements()) {
            JarEntry entry = entries.nextElement();
            String jarEntry = entry.getName();
            if (jarEntry.startsWith(jarEntryStart)) {
                ZipEntry ze = jar.getEntry(jarEntry);
                File bin = new File(path + "/" + jarEntry);
                IOUtils.copy(jar.getInputStream(ze), new FileOutputStream(bin));
            }
        }
    } catch (Exception e) {
        e.printStackTrace();
    }
}

