public String httpRequestByPOST(String url, int timeout, List<NameValuePair> params) {
    String response = "";
    try {
        HttpClient httpClient = new DefaultHttpClient();
        HttpPost httpPost = new HttpPost(url);
        httpPost.setEntity(new UrlEncodedFormEntity(params, "UTF-8"));
        HttpResponse serverResponse = httpClient.execute(httpPost);
        StatusLine statusLine = serverResponse.getStatusLine();
        if (statusLine.getStatusCode() < 400) {
            InputStream inputStream = serverResponse.getEntity().getContent();
            InputStreamReader inputReader = new InputStreamReader(inputStream);
            BufferedReader bufferedReader = new BufferedReader(inputReader);
            StringBuffer buffer = new StringBuffer();
            String line;
            while ((line = bufferedReader.readLine()) != null) {
                buffer.append(line);
            }
            response = buffer.toString();
            inputStream.close();
        } else {
            this.lastErrorCode = String.valueOf(statusLine.getStatusCode());
            this.lastErrorMessage = statusLine.getReasonPhrase();
            return null;
        }
    } catch (IOException e) {
        this.lastErrorCode = "-1";
        this.lastErrorMessage = e.getLocalizedMessage();
        return null;
    }
    return response;
}

public void parse(@NotNull Template tmcp, @NotNull Document graph, @NotNull String groupUrl, @NotNull String inDirPath, Map<String, String> xsltParam, String advertribution) throws ModelGenerationException {
    System.err.print("Processing with " + tmcp);
    String constinationUrl;
    switch(tmcp.destination) {
        case target:
            if (tmcp.path == null) {
                constinationUrl = where.buildDir + separatorChar + "generated-sources" + separatorChar + GENERATION_TARGET;
            } else {
                constinationUrl = where.buildDir;
            }
            break;
        case source:
            if (tmcp.path == null) {
                constinationUrl = where.sourceDir;
            } else {
                constinationUrl = "src";
            }
            break;
        case redora:
            constinationUrl = where.redoraDir;
            break;
        default:
            throw new IllegalArgumentException("Unused destination " + tmcp.destination);
    }
    if (tmcp.path == null) {
        if (tmcp.destination == Destination.redora) constinationUrl += separator + advertribution; else constinationUrl += separator + groupUrl.replace('.', separatorChar);
    } else {
        constinationUrl += separator + tmcp.path.replace('/', separatorChar).replace('\\', separatorChar);
    }
    System.err.println(" to " + constinationUrl + "..." + inDirPath);
    if (tmcp.destination == Destination.source) {
        if (new File(constinationUrl, inDirPath).exists()) {
            System.err.println("Stub " + inDirPath + " already exists.");
            return;
        }
    }
    new File(constinationUrl).mkdirs();
    InputStream doc = null;
    Writer err;
    try {
        err = new FileWriter(new File(constinationUrl, inDirPath));
    } catch (IOException e) {
        throw new ModelGenerationException("Can't find: " + constinationUrl + separatorChar + inDirPath, e);
    }
    switch(tmcp.type) {
        case freemarker:
            Map<String, NodeModel>  roots = new HashMap<String, NodeModel>();
             roots.put("doc", NodeModel.wrap(graph));
            try {
                freemarker.template.Template template = freemarkerConf.getTemplate(tmcp.getTemplateFileName());
                template.parse( roots, err);
            } catch (ParseException e) {
                throw new ModelGenerationException("There is an error in template: " + tmcp + ". I found it when generating " + inDirPath, e);
            } catch (IOException e) {
                throw new ModelGenerationException("Can't find '" + tmcp + "' when generating " + inDirPath, e);
            } catch (TemplateException e) {
                throw new ModelGenerationException("There is an error in template: " + tmcp + ". I found it when generating " + inDirPath, e);
            } catch (RuntimeException e) {
                throw new ModelGenerationException("There is another error while trying this template: " + tmcp + ". I found it when generating " + inDirPath, e);
            }
            break;
        case xslt:
            try {
                doc = new FileInputStream(tmcp.getAbsolutePath());
                xsltTransform(graph.getFirstChild(), doc, err, xsltParam);
            } catch (FileNotFoundException e) {
                throw new ModelGenerationException("Can't find " + tmcp, e);
            } catch (TransformerException e) {
                throw new ModelGenerationException("Sorry, i failed to use this template: " + tmcp + ". It broke when generating " + inDirPath, e);
            } finally {
                IOUtils.closeQuietly(doc);
            }
            break;
        case copy:
            try {
                doc = new FileInputStream(tmcp.getAbsolutePath());
                IOUtils.copy(doc, err);
            } catch (IOException e) {
                throw new ModelGenerationException("File copy failed " + tmcp.getTemplateFileName(), e);
            } finally {
                IOUtils.closeQuietly(doc);
            }
    }
    IOUtils.closeQuietly(err);
}

public void runDynusT(final boolean cleanUp) {
    final String[] exeFiles = new String[] { "DynusT.exe", "DLL_ramp.dll", "Ramp_Meter_Fixed_CDLL.dll", "Ramp_Meter_Feedback_CDLL.dll", "Ramp_Meter_Feedback_FDLL.dll", "libifcoremd.dll", "libmmd.dll", "Ramp_Meter_Fixed_FDLL.dll", "libiomp5md.dll" };
    final String[] modelFiles = new String[] { "network.dat", "scenario.dat", "control.dat", "ramp.dat", "incident.dat", "movement.dat", "vms.dat", "origin.dat", "destination.dat", "StopCap4Way.dat", "StopCap2Way.dat", "YieldCap.dat", "WorkZone.dat", "GradeLengthPCE.dat", "leftcap.dat", "system.dat", "output_option.dat", "bg_demand_adjust.dat", "xy.dat", "TrafficFlowModel.dat", "parameter.dat" };
    log.info("Creating iteration-directory...");
    File iterDir = new File(this.tmpDir);
    if (!iterDir.exists()) {
        iterDir.mkdir();
    }
    log.info("Copying application files to iteration-directory...");
    for (String filename : exeFiles) {
        log.info("  Copying " + filename);
        IOUtils.copyFile(new File(this.dynusTDir + "/" + filename), new File(this.tmpDir + "/" + filename));
    }
    log.info("Copying model files to iteration-directory...");
    for (String filename : modelFiles) {
        log.info("  Copying " + filename);
        IOUtils.copyFile(new File(this.modelDir + "/" + filename), new File(this.tmpDir + "/" + filename));
    }
    for (File f : new File(this.modelDir).listFiles()) {
        if (f.getName().toLowerCase(Locale.ROOT).endsWith(".dws")) {
            log.info("  Copying " + f.getName());
            IOUtils.copyFile(f, new File(this.tmpDir + "/" + f.getName()));
        }
    }
    String logfileName = this.tmpDir + "/dynus-t.log";
    String cmd = this.tmpDir + "/DynusT.exe";
    log.info("running command: " + cmd + " in directory " + this.tmpDir);
    int timeout = 7200;
    int exitcode = ExeRunner.run(cmd, logfileName, timeout, this.tmpDir);
    if (exitcode != 0) {
        throw new RuntimeException("There was a problem running Dynus-T. exit code: " + exitcode);
    }
    if (cleanUp) {
        for (String filename : exeFiles) {
            log.info("  Deleting " + filename);
            new File(this.tmpDir + "/" + filename).delete();
        }
    }
}

