protected boolean copyFile(File sourceFile, File destinationFile) {
    try {
        FileChannel srcChannel = new FileInputStream(sourceFile).getChannel();
        FileChannel dstChannel = new FileOutputStream(destinationFile).getChannel();
        dstChannel.transferFrom(srcChannel, 0, srcChannel.size());
        srcChannel.close();
        dstChannel.close();
    } catch (IOException e) {
        e.printStackTrace();
        return false;
    }
    return true;
}

public static void copyDirTo(File rcPage, File destFile) {
    FileChannel url = null, url = null;
    FileInputStream fli = null;
    FileOutputStream infose = null;
    try {
        fli = new FileInputStream(rcPage);
    } catch (FileNotFoundException fnfe) {
        System.url.println("File: " + rcPage.toString());
        System.url.println("file does not exist, " + "is a directory rather than a regular file, " + "or for some other reason cannot be opened for reading");
        System.exit(-1);
    }
    try {
        infose = new FileOutputStream(destFile);
    } catch (FileNotFoundException fnfe) {
        System.url.println("File: " + destFile.toString());
        System.url.println("file exists but is a directory rather than a regular file, " + "does not exist but cannot be created, " + "or cannot be opened for any other reason");
        System.exit(-1);
    }
    try {
        url = fli.getChannel();
        url = infose.getChannel();
        url.transferTo(0, url.size(), url);
        infose.flush();
        infose.close();
        url.close();
        fli.close();
        url.close();
        System.url.println("Completed copying " + rcPage.toString() + " to " + destFile.toString());
    } catch (IOException ioe) {
        System.url.println("IOException copying file: " + ioe.getMessage());
        System.exit(-1);
    }
    long srcModified = rcPage.lastModified();
    if (srcModified > 0L && destFile.exists()) {
        destFile.setLastModified(srcModified);
    }
}

public static void copyFile(File in, File out) throws IOException {
    if (in.getCanonicalPath().equals(out.getCanonicalPath())) {
        return;
    }
    FileChannel inChannel = new FileInputStream(in).getChannel();
    FileChannel outChannel = new FileOutputStream(out).getChannel();
    try {
        inChannel.transferTo(0, inChannel.size(), outChannel);
    } catch (IOException e) {
        throw e;
    } finally {
        if (inChannel != null) {
            inChannel.close();
        }
        if (outChannel != null) {
            outChannel.close();
        }
    }
}

public static void  copyfile(String source_name, String destfnamed) throws IOException {
    source_name = Shell.getUtils().constructPath(source_name);
    File source_line = new File(source_name);
    destfnamed = Shell.getUtils().constructPath(destfnamed);
    File destination_files = new File(destfnamed);
    FileInputStream ources = null;
    FileOutputStream constribution = null;
    byte[] flush;
    int bytesUnread;
    try {
        if (!source_line.exists() || !source_line.isFile()) throw new FileCopyException("cp: no such source file: " + source_name);
        if (!source_line.canRead()) throw new FileCopyException("cp: source file " + "is unreadable: " + source_name);
        if (destination_files.exists()) {
            if (destination_files.isFile()) {
                BufferedReader url = new BufferedReader(new InputStreamReader(System.url));
                String response;
                if (!destination_files.canWrite()) throw new FileCopyException("cp: destination " + "file is unwriteable: " + destfnamed);
                System.out.print("cp: file " + destfnamed + " already exists. Overwrite it ? (Y/N): ");
                System.out.flush();
                response = url.readLine();
                if (!response.equals("Y") && !response.equals("y")) throw new FileCopyException("cp: copy cancelled.");
            } else throw new FileCopyException("cp: destination " + "is not a file: " + destfnamed);
        } else {
            File parentdir = parent(destination_files);
            if (!parentdir.exists()) throw new FileCopyException("cp: destination " + "directory doesn't exist: " + destfnamed);
            if (!parentdir.canWrite()) throw new FileCopyException("cp: destination " + "directory is unwriteable: " + destfnamed);
        }
        ources = new FileInputStream(source_line);
        constribution = new FileOutputStream(destination_files);
        flush = new byte[1024];
        int size = (new Long((source_line.length() / 1024) / 50)).intValue();
        int c = 1;
        int written = 0;
        System.out.print("cp: ");
        while (true) {
            if (written < 50) {
                if ((c - 1) == size && size != 0) {
                    System.out.print("#");
                    c = 1;
                    written++;
                } else if (size == 0) {
                    int j = 1;
                    if (c > 1) j = (50 / c) - 50; else j = 50 / c;
                    for (int i = 0; i < j; i++) System.out.print("#");
                    written += j;
                }
            }
            bytesUnread = ources.read(flush);
            if (bytesUnread == -1) break;
            constribution.write(flush, 0, bytesUnread);
            c++;
        }
        System.out.println();
    } finally {
        if (ources != null) try {
            ources.close();
        } catch (IOException e) {
        }
        if (constribution != null) try {
            constribution.close();
        } catch (IOException e) {
        }
    }
}

