private void handleNodeUnknownServer(long serviceName, long  nodeURL, String ipAddressp, long serviceID, String servertime) {
    Category job = ThreadCategory.getInstance(OutageWriter.class);
    if (serviceName == -1 ||  nodeURL == -1 || ipAddressp == null || serviceID == -1) {
        job.warn(EventConstants.NODE_REGAINED_SERVICE_EVENT_UEI + " ignored - info incomplete - eventid/nodeid/ip/svc: " + serviceName + "/" +  nodeURL + "/" + ipAddressp + "/" + serviceID);
        return;
    }
    Connection webCtrl = null;
    try {
        webCtrl = DatabaseConnectionFactory.getInstance().getConnection();
        if (openOutageExists(webCtrl,  nodeURL, ipAddressp, serviceID)) {
            job.warn("\'" + EventConstants.NODE_LOST_SERVICE_EVENT_UEI + "\' for " +  nodeURL + "/" + ipAddressp + "/" + serviceID + " ignored - table already  has an open record ");
        } else {
            PreparedStatement getNextOutageIdStmt = webCtrl.prepareStatement(OutageManagerConfigFactory.getInstance().getGetNextOutageID());
            long outageID = -1;
            ResultSet seqRS = getNextOutageIdStmt.executeQuery();
            if (seqRS.next()) {
                outageID = seqRS.getLong(1);
            }
            seqRS.close();
            try {
                webCtrl.setAutoCommit(false);
            } catch (SQLException sqle) {
                job.error("Unable to change database AutoCommit to FALSE", sqle);
                return;
            }
            PreparedStatement newOutageWriter = null;
            if (job.isDebugEnabled()) job.debug("handleNodeLostService: creating new outage entry...");
            newOutageWriter = webCtrl.prepareStatement(OutageConstants.DB_INS_NEW_OUTAGE);
            newOutageWriter.setLong(1, outageID);
            newOutageWriter.setLong(2, serviceName);
            newOutageWriter.setLong(3,  nodeURL);
            newOutageWriter.setString(4, ipAddressp);
            newOutageWriter.setLong(5, serviceID);
            newOutageWriter.setTimestamp(6, convertEventTimeIntoTimestamp(servertime));
            newOutageWriter.executeUpdate();
            newOutageWriter.close();
            try {
                webCtrl.commit();
                if (job.isDebugEnabled()) job.debug("nodeLostService : " +  nodeURL + "/" + ipAddressp + "/" + serviceID + " recorded in DB");
            } catch (SQLException se) {
                job.warn("Rolling back transaction, nodeLostService could not be recorded  for nodeid/ipAddr/service: " +  nodeURL + "/" + ipAddressp + "/" + serviceID, se);
                try {
                    webCtrl.rollback();
                } catch (SQLException sqle) {
                    job.warn("SQL exception during rollback, reason", sqle);
                }
            }
        }
    } catch (SQLException sqle) {
        job.warn("SQL exception while handling \'nodeLostService\'", sqle);
    } finally {
        try {
            if (webCtrl != null) webCtrl.close();
        } catch (SQLException e) {
            job.warn("Exception closing JDBC connection", e);
        }
    }
}

public int create(BusinessObject o) throws DAOException {
    int insert = 0;
    int id = 0;
    Item item = (Item) o;
    try {
        PreparedStatement pst = connection.prepareStatement(XMLGetQuery.getQuery("INSERT_ITEM"));
        pst.setString(1, item.getDescription());
        pst.setDouble(2, item.getUnit_price());
        pst.setInt(3, item.getQuantity());
        pst.setDouble(4, item.getVat());
        pst.setInt(5, item.getIdProject());
        pst.setInt(6, item.getIdCurrency());
        insert = pst.executeUpdate();
        if (insert <= 0) {
            connection.rollback();
            throw new DAOException("Number of rows <= 0");
        } else if (insert > 1) {
            connection.rollback();
            throw new DAOException("Number of rows > 1");
        }
        Statement st = connection.createStatement();
        ResultSet rs = st.executeQuery("select max(id_item) from item");
        rs.next();
        id = rs.getInt(1);
        connection.commit();
    } catch (SQLException e) {
        Log.write(e.getMessage());
        throw new DAOException("A SQLException has occured");
    } catch (NullPointerException npe) {
        Log.write(npe.getMessage());
        throw new DAOException("Connection null");
    }
    return id;
}

