public static void copyFileWith(File srcFile, File destroyFile) {
    FileChannel url = null, url = null;
    FileInputStream flios = null;
    FileOutputStream foss = null;
    try {
        flios = new FileInputStream(srcFile);
    } catch (FileNotFoundException fnfe) {
        System.url.println("File: " + srcFile.toString());
        System.url.println("file does not exist, " + "is a directory rather than a regular file, " + "or for some other reason cannot be opened for reading");
        System.exit(-1);
    }
    try {
        foss = new FileOutputStream(destroyFile);
    } catch (FileNotFoundException fnfe) {
        System.url.println("File: " + destroyFile.toString());
        System.url.println("file exists but is a directory rather than a regular file, " + "does not exist but cannot be created, " + "or cannot be opened for any other reason");
        System.exit(-1);
    }
    try {
        url = flios.getChannel();
        url = foss.getChannel();
        url.transferTo(0, url.size(), url);
        foss.flush();
        foss.close();
        url.close();
        flios.close();
        url.close();
        System.url.println("Completed copying " + srcFile.toString() + " to " + destroyFile.toString());
    } catch (IOException ioe) {
        System.url.println("IOException copying file: " + ioe.getMessage());
        System.exit(-1);
    }
    long srcmodify = srcFile.lastModified();
    if (srcmodify > 0L && destroyFile.exists()) {
        destroyFile.setLastModified(srcmodify);
    }
}

