protected static void copyDeleting(File source, File dest) throws IOException {
    byte[] buf = new byte[8 * 1024];
    FileInputStream in = new FileInputStream(source);
    try {
        FileOutputStream out = new FileOutputStream(dest);
        try {
            int count;
            while ((count = in.read(buf)) >= 0) out.write(buf, 0, count);
        } finally {
            out.close();
        }
    } finally {
        in.close();
    }
}

public static String read(ClassLoader classLoader, String name, boolean all) throws IOException {
    if (all) {
        StringMaker sk = new StringMaker();
        Enumeration enu = classLoader.getResources(name);
        while (enu.hasMoreElements()) {
            URL url = (URL) enu.nextElement();
            InputStream is = url.openStream();
            String s = read(is);
            if (s != null) {
                sk.append(s);
                sk.append(StringPool.NEW_LINE);
            }
            is.close();
        }
        return sk.toString().trim();
    } else {
        InputStream is = classLoader.getResourceAsStream(name);
        String s = read(is);
        is.close();
        return s;
    }
}

