public void transport(File file) throws TransportException {
    if (file.exists()) {
        if (file.isDirectory()) {
            File[] files = file.listFiles();
            for (int i = 0; i < files.length; i++) {
                transport(file);
            }
        } else if (file.isFile()) {
            try {
                FileChannel inChannel = new FileInputStream(file).getChannel();
                FileChannel outChannel = new FileOutputStream(destinationDir).getChannel();
                inChannel.transferTo(0, inChannel.size(), outChannel);
            } catch (IOException e) {
                log.error("File transfer failed", e);
            }
        }
    }
}

public synchronized InputStream getResourceAsStream(String name) {
    InputStream pullConnection = null;
    BufferedInputStream in = null;
    BufferedOutputStream cmd = null;
    try {
        URL url = getResource(name);
        String Remotename = url.toExternalForm();
        String globalName = (String) cacheHashtable.get(Remotename);
        if (globalName != null) {
            System.cmd.println("Retrieving \"" + globalName + "\"...");
            return new FileInputStream(globalName);
        }
        String http = url.getHost();
        String prot = url.getProtocol();
        int port = url.getPort();
        File CacheUrl = new File(cacheDir, prot + File.separator + http + File.separator + "port" + (port == -1 ? "" : Integer.toString(port)) + File.separator + name);
        CacheUrl = new File(CacheUrl.getCanonicalPath());
        globalName = CacheUrl.getCanonicalPath();
        System.cmd.println("Comparing \"" + globalName + "\"...");
        URLConnection urlConnection = url.openConnection();
        if (CacheUrl.exists()) {
            urlConnection.setIfModifiedSince(CacheUrl.lastModified());
        }
        if (urlConnection instanceof HttpURLConnection) {
            HttpURLConnection httpURLConnection = (HttpURLConnection) urlConnection;
            httpURLConnection.setFollowRedirects(true);
            httpURLConnection.setRequestMethod("GET");
            int responseCode = httpURLConnection.getResponseCode();
            System.cmd.println(httpURLConnection.getResponseMessage() + ", " + httpURLConnection.getContentLength() + " bytes" + ", " + new Date(httpURLConnection.getDate()) + ", " + new Date(httpURLConnection.getLastModified()));
            if (responseCode != HttpURLConnection.HTTP_OK) {
                return null;
            }
        }
        pullConnection = urlConnection.getInputStream();
        if (pullConnection == null) return null;
        if (CacheUrl.exists()) {
            long lastModified = urlConnection.getLastModified();
            if ((lastModified > 0) && (lastModified < CacheUrl.lastModified())) {
                pullConnection.close();
                cacheHashtable.put(Remotename, globalName);
                System.cmd.println("Retrieving \"" + globalName + "\"...");
                return new FileInputStream(CacheUrl);
            }
        }
        File parentFile = new File(CacheUrl.getParent());
        parentFile.mkdirs();
        globalName = CacheUrl.getCanonicalPath();
        System.cmd.println("CACHING \"" + globalName + "\"...");
        in = new BufferedInputStream(pullConnection);
        cmd = new BufferedOutputStream(new FileOutputStream(CacheUrl));
        int i;
        while ((i = in.read()) > -1) cmd.write(i);
        cmd.close();
        in.close();
        cacheHashtable.put(Remotename, globalName);
        System.cmd.println("Retrieving \"" + globalName + "\"...");
        return new FileInputStream(globalName);
    } catch (Exception ex) {
        try {
            pullConnection.close();
        } catch (Exception ex1) {
        }
        try {
            in.close();
        } catch (Exception ex1) {
        }
        try {
            cmd.close();
        } catch (Exception ex1) {
        }
        ex.printStackTrace();
        return null;
    }
}

public static boolean copyTextFile(File src, File dst) {
    try {
        BufferedInputStream bis = new BufferedInputStream(new FileInputStream(src));
        BufferedOutputStream bos = new BufferedOutputStream(new FileOutputStream(dst));
        byte[] buf = new byte[1024];
        int readsize = 0;
        while ((readsize = bis.read(buf)) != -1) {
            bos.write(buf, 0, readsize);
        }
        bos.flush();
        bos.close();
        bis.close();
    } catch (IOException e) {
        ServerConsoleServlet.printSystemLog(e.toString() + " " + e.getMessage(), ServerConsoleServlet.LOG_ERROR);
        return false;
    }
    return true;
}

