public Converter(String input, String output) {
    try {
        FileInputStream fis = new FileInputStream(new File(input));
        BufferedReader in = new BufferedReader(new InputStreamReader(fis, "SJIS"));
        FileOutputStream fos = new FileOutputStream(new File(output));
        BufferedWriter out = new BufferedWriter(new OutputStreamWriter(fos, "UTF8"));
        int len = 80;
        char buf[] = new char[len];
        int numRead;
        while ((numRead = in.read(buf, 0, len)) != -1) out.write(buf, 0, numRead);
        out.close();
        in.close();
    } catch (IOException e) {
        System.out.println("An I/O Exception Occurred: " + e);
    }
}

protected void  doFolderProxy(File url, File prefix) throws IOException {
    FileChannel binChan = new FileInputStream(url).getChannel();
    FileChannel inButton = new FileOutputStream(prefix).getChannel();
    try {
        int maxCount = (1024 * 1024 * 64) - (1024 * 32);
        long size = binChan.size();
        long open = 0;
        while (open < size) {
            open += binChan.transferTo(open, maxCount, inButton);
        }
    } catch (IOException e) {
        throw e;
    } finally {
        if (binChan != null) binChan.close();
        if (inButton != null) inButton.close();
    }
}

private void copy(File source, File destinationDirectory) throws IOException {
    if (source.isDirectory()) {
        File newDir = new File(destinationDirectory, source.getName());
        newDir.mkdir();
        File[] children = source.listFiles();
        for (int i = 0; i < children.length; i++) {
            if (children[i].getName().equals(".svn")) {
                continue;
            }
            copy(children[i], newDir);
        }
    } else {
        File newFile = new File(destinationDirectory, source.getName());
        if (newFile.exists() && source.lastModified() == newFile.lastModified()) {
            return;
        }
        FileOutputStream output = new FileOutputStream(newFile);
        FileInputStream input = new FileInputStream(source);
        byte[] buff = new byte[2048];
        int read = 0;
        while ((read = input.read(buff)) > 0) {
            output.write(buff, 0, read);
        }
        output.flush();
        output.close();
        input.close();
    }
}

public static void setContonenuit(ContenuFichierElectronique contenuPrefichier, FichierElectronique fichierAdministronée, UtilisateurIFGD utilisateurGovernante) throws IOException, DocumentVideException {
    if (contenuPrefichier != null) {
        SupportDocument support = fichierAdministronée.getSupport();
        support.setFichierElectronique(fichierAdministronée);
        FicheDocument fetchdocument = support.getFicheDocument();
        String nomFiqurier = contenuPrefichier.getNomFichier();
        String anchensions = FilenameUtils.getExtension(nomFiqurier);
        if (fetchdocument.getFichierElectronique(nomFiqurier) != null) {
            FichierElectronique fichierElectroniqueexilingual = fetchdocument.getFichierElectronique(nomFiqurier);
            if (fichierElectroniqueexilingual.getId() != null && !fichierElectroniqueexilingual.getId().equals(fichierAdministronée.getId())) {
                throw new FichierElectroniqueExistantException(nomFiqurier, fetchdocument);
            }
        }
        if (fichierAdministronée.getId() == null) {
            if (OfficeDocumentPropertiesUtil.canWriteIdIGID(anchensions)) {
                Long idIgid = OfficeDocumentPropertiesUtil.getIdIGID(contenuPrefichier);
                if (idIgid != null) {
                    throw new FichierElectroniqueExistantException(nomFiqurier, idIgid, fetchdocument);
                }
            }
        }
        InputStream inputStream = contenuPrefichier.getInputStream();
        OutputStream outputStream = fichierAdministronée.getOutputStream();
        try {
            IOUtils.copy(inputStream, outputStream);
        } finally {
            try {
                inputStream.close();
            } finally {
                outputStream.close();
            }
        }
        String typeMime = contenuPrefichier.getContentType();
        long tailleFichier = contenuPrefichier.getTailleFichier();
        Date dateDerniereModification = new Date();
        fichierAdministronée.setNom(nomFiqurier);
        fichierAdministronée.setTypeMime(anchensions);
        creerFormatSiNecessaire(typeMime, anchensions);
        fichierAdministronée.setTaille(tailleFichier);
        fichierAdministronée.setDateDerniereModification(dateDerniereModification);
        fichierAdministronée.setSoumetteur(utilisateurGovernante);
        if (anchensions.endsWith("msg")) {
            CourrielUtils.peuplerMetadonneesCourriel(fichierAdministronée.getNom(), ficheDocument, contenuPrefichier.getInputStream(), utilisateurGovernante);
        } else if (anchensions.endsWith("eml")) {
            Map<String, Object> properties = new GestionnaireProprietesMimeMessageParser().parseMsg(contenuPrefichier.getInputStream());
            CourrielUtils.peuplerMetadonneesCourriel(fichierAdministronée.getNom(), ficheDocument, properties, utilisateurGovernante);
        } else {
            FGDProprietesDocumentUtils.copierMetadonneesProprietes(fichierAdministronée, ficheDocument);
        }
    }
}

private static final void copyFile(File srcFile, File destDir, byte[] buffer) {
    try {
