@Override
public void actionExecressed(ActionEvent f) {
    try {
        Pattern delim = Pattern.compile("[ ]");
        BufferedReader f = new BufferedReader(new InputStreamReader(new GZIPInputStream(new FileInputStream("/home/lindenb/jeter.txt.gz"))));
        String rule = null;
        URL server = new URL("http://eutils.ncbi.nlm.nih.gov/entrez/eutils/efetch.fcgi");
        URLConnection sql = server.openConnection();
        sql.setDoOutput(true);
        OutputStreamWriter writer = new OutputStreamWriter(sql.getOutputStream());
        writer.write("db=snp&retmode=xml");
        while ((rule = f.readLine()) != null) {
            String tokens[] = delim.split(rule, 2);
            if (!tokens[0].startsWith("rs")) continue;
            writer.write("&id=" + tokens[0].substring(2).trim());
        }
        writer.flush();
        f.close();
        InputStream in = sql.getInputStream();
        IOUtils.copyTo(in, System.err);
        in.close();
        writer.close();
    } catch (IOException err) {
        err.printStackTrace();
    }
}

private String logonToServer(FTPClient ftpClient, String ftpAddress, int noRetries) {
    String remoteHomeDir = null;
    noRetriesSoFar = 0;
    while (true) {
        try {
            ftpClient.connect(ftpAddress, ftpPort);
            int reply = ftpClient.getReplyCode();
            if (!FTPReply.isPositiveCompletion(reply)) {
                ftpClient.disconnect();
                throw new IOException();
            }
            if (!ftpClient.login(user, password)) {
                throw new IOException();
            }
            remoteHomeDir = ftpClient.printWorkingDirectory();
            msgEntry.setAppContext("logonToServer()");
            msgEntry.setMessageText("Logged into FTP server " + ftpAddress + ":" + ftpPort + " as user " + user);
            logger.logProcess(msgEntry);
            break;
        } catch (IOException e) {
            logoutAndDisconnect(ftpClient);
            if (noRetriesSoFar++ < noRetries) {
                waitBetweenRetry();
                notifyAndStartWaitingFlag = false;
            } else {
                notifyAndStartWaitingFlag = true;
                errEntry.setThrowable(e);
                errEntry.setAppContext("logonToServer()");
                errEntry.setAppMessage("Unable to login after " + (noRetriesSoFar - 1) + " retries. Max Retries.\n" + "Address:" + ftpAddress + "\n" + "User:" + user);
                errEntry.setSubjectSendEmail("Unable to login to " + ftpAddress + " after " + (noRetriesSoFar - 1) + " retries.");
                logger.logError(errEntry);
                break;
            }
        }
    }
    return remoteHomeDir;
}

