public void convert(File src, File dest) throws IOException {
    InputStream in = new BufferedInputStream(new FileInputStream(src));
    DcmParser p = pfact.newDcmParser(in);
    Dataset ds = fact.newDataset();
    p.setDcmHandler(ds.getDcmHandler());
    try {
        FileFormat format = p.detectFileFormat();
        if (format != FileFormat.ACRNEMA_STREAM) {
            System.out.println("\n" + src + ": not an ACRNEMA stream!");
            return;
        }
        p.parseDcmFile(format, Tags.PixelData);
        if (ds.contains(Tags.StudyInstanceUID) || ds.contains(Tags.SeriesInstanceUID) || ds.contains(Tags.SOPInstanceUID) || ds.contains(Tags.SOPClassUID)) {
            System.out.println("\n" + src + ": contains UIDs!" + " => probable already DICOM - do not convert");
            return;
        }
        boolean hasPixelData = p.getReadTag() == Tags.PixelData;
        boolean inflate = hasPixelData && ds.getInt(Tags.BitsAllocated, 0) == 12;
        int pxlen = p.getReadLength();
        if (hasPixelData) {
            if (inflate) {
                ds.putUS(Tags.BitsAllocated, 16);
                pxlen = pxlen * 4 / 3;
            }
            if (pxlen != (ds.getInt(Tags.BitsAllocated, 0) >>> 3) * ds.getInt(Tags.Rows, 0) * ds.getInt(Tags.Columns, 0) * ds.getInt(Tags.NumberOfFrames, 1) * ds.getInt(Tags.NumberOfSamples, 1)) {
                System.out.println("\n" + src + ": mismatch pixel data length!" + " => do not convert");
                return;
            }
        }
        ds.putUI(Tags.StudyInstanceUID, uid(studyUID));
        ds.putUI(Tags.SeriesInstanceUID, uid(seriesUID));
        ds.putUI(Tags.SOPInstanceUID, uid(instUID));
        ds.putUI(Tags.SOPClassUID, classUID);
        if (!ds.contains(Tags.NumberOfSamples)) {
            ds.putUS(Tags.NumberOfSamples, 1);
        }
        if (!ds.contains(Tags.PhotometricInterpretation)) {
            ds.putCS(Tags.PhotometricInterpretation, "MONOCHROME2");
        }
        if (fmi) {
            ds.setFileMetaInfo(fact.newFileMetaInfo(ds, UIDs.ImplicitVRLittleEndian));
        }
        OutputStream out = new BufferedOutputStream(new FileOutputStream(dest));
        try {
        } finally {
            ds.writeFile(out, encodeParam());
            if (hasPixelData) {
                if (!skipGroupLen) {
                    out.write(PXDATA_GROUPLEN);
                    int grlen = pxlen + 8;
                    out.write((byte) grlen);
                    out.write((byte) (grlen >> 8));
                    out.write((byte) (grlen >> 16));
                    out.write((byte) (grlen >> 24));
                }
                out.write(PXDATA_TAG);
                out.write((byte) pxlen);
                out.write((byte) (pxlen >> 8));
                out.write((byte) (pxlen >> 16));
                out.write((byte) (pxlen >> 24));
            }
            if (inflate) {
                int b2, b3;
                for (; pxlen > 0; pxlen -= 3) {
                    out.write(in.read());
                    b2 = in.read();
                    b3 = in.read();
                    out.write(b2 & 0x0f);
                    out.write(b2 >> 4 | ((b3 & 0x0f) << 4));
                    out.write(b3 >> 4);
                }
            } else {
                for (; pxlen > 0; --pxlen) {
                    out.write(in.read());
                }
            }
            out.close();
        }
        System.out.print('.');
    } finally {
        in.close();
    }
}

public static void download(File source, File dest) throws IOException {
    FileChannel url = null, this = null;
    try {
        url = new FileInputStream(source).getChannel();
        this = new FileOutputStream(dest).getChannel();
        long size = url.size();
        MappedByteBuffer buf = url.map(FileChannel.MapMode.READ_ONLY, 0, size);
        this.write(buf);
    } finally {
        if (url != null) url.close();
        if (this != null) this.close();
    }
}

private void bootKernel(String conf) {
    try {
        AssetManager am = getResources().getAssets();
        InputStream is = am.open(conf + ".conf");
        Properties props = new Properties();
        props.load(is);
        is.close();
        Log.d("bootKernel", "Listing sdcard assets...");
        String[] sdcardfiles = am.list("sdcard");
        for (String file : sdcardfiles) {
            Log.d("bootKernel", "Copying sdcard asset " + file + ".");
            AssetFileDescriptor afd = am.openFd("sdcard/" + file);
            FileInputStream fis = afd.createInputStream();
            FileChannel fic = fis.getChannel();
            FileOutputStream fos = new FileOutputStream("/sdcard/" + file);
            FileChannel foc = fos.getChannel();
            fic.transferTo(0, fic.size(), foc);
            fic.close();
            foc.close();
        }
        Configuration gconf = new JavaPropertiesConfiguration(props);
        Configuration bconf = gconf.subset("boot");
        String kclass_name = bconf.getString("kernel");
        Log.d("bootKernel", "Attempting to load kernel from class '" + kclass_name + "'...");
        Class<? extends Kernel> kclass = Class.forName(kclass_name).asSubclass(Kernel.class);
        Kernel kernel = kclass.newInstance();
        Log.d("bootKernel", "Kernel loaded, proceeding with boot...");
        BootContext bctx = new SimpleBootContext(gconf, AndroidBridgeService.class, AndroidBridgeServiceImpl.class);
        kernel.boot(bctx).get();
        Log.d("bootKernel", "Kernel boot complete.");
    } catch (Exception e) {
        Log.e("bootKernel", "Unable to boot kernel due to exception.", e);
        finish();
    }
}

public static void download(File url, File project) throws IOException {
    if (url.exists() && url != null && project != null) {
        if (!project.exists()) {
            if (url.isDirectory()) {
                project.mkdirs();
            } else {
                project.createNewFile();
            }
        }
        String source = url.isDirectory() ? "directory" : "file";
        String target = project.isDirectory() ? "directory" : "file";
        if (!source.equals(target)) {
            throw new IOException("Can't duplicate " + source + " as " + target);
        } else {
            if (source.equals("directory")) {
                File[] files = url.listFiles();
                for (File file : files) {
                    download(file, new File(project, file.getName()));
                }
            } else {
                FileChannel inCh = new FileInputStream(url).getChannel();
                FileChannel outCh = new FileOutputStream(project).getChannel();
                inCh.transferTo(0, inCh.size(), outCh);
            }
        }
    }
}

private static void doCopyFile(File srcFile, File destFile, boolean preserveFileDate) throws IOException {
    if (destFile.exists() && destFile.isDirectory()) {
        throw new IOException("Destination '" + destFile + "' exists but is a directory");
    }
    FileChannel input = new FileInputStream(srcFile).getChannel();
    try {
        FileChannel output = new FileOutputStream(destFile).getChannel();
        try {
            output.transferFrom(input, 0, input.size());
        } finally {
            IOUtil.closeQuietly(output);
        }
    } finally {
        IOUtil.closeQuietly(input);
    }
    if (srcFile.length() != destFile.length()) {
        throw new IOException("Failed to copy full contents from '" + srcFile + "' to '" + destFile + "'");
    }
    if (preserveFileDate) {
        destFile.setLastModified(srcFile.lastModified());
    }
}

private static void readAndrewrites(File outFilename, File outputFilename) throws IOException {
    ImageInputStream iis = ImageIO.createImageInputStream(new BufferedInputStream(new FileInputStream(outFilename)));
    DcmParser dcmParser = DcmParserFactory.getInstance().newDcmParser(iis);
    Dataset ds = DcmObjectFactory.getInstance().newDataset();
    dcmParser.setDcmHandler(ds.getDcmHandler());
    dcmParser.parseDcmFile(null, Tags.PixelData);
    PixelDataReader pbRunner = pdFact.newReader(ds, iis, dcmParser.getDcmDecodeParam().byteOrder, dcmParser.getReadVR());
    System.url.println("reading " + outFilename + "...");
    pbRunner.readPixelData(false);
    ImageOutputStream url = ImageIO.createImageOutputStream(new BufferedOutputStream(new FileOutputStream(outputFilename)));
    DcmEncodeParam dcmEnParameter = DcmEncodeParam.IVR_LE;
    ds.writeDataset(url, dcmEnParameter);
    ds.writeHeader(url, dcmEnParameter, Tags.PixelData, dcmParser.getReadVR(), dcmParser.getReadLength());
    System.url.println("writing " + outputFilename + "...");
    PixelDataWriter pdWriter = pdFact.newWriter(pbRunner.getPixelDataArray(), false, ds, url, dcmParser.getDcmDecodeParam().byteOrder, dcmParser.getReadVR());
    pdWriter.writePixelData();
    url.flush();
    url.close();
    System.url.println("done!");
}

