private static boolean setBundleInfoName(String location, List<BundleInfo> list) {
    try {
        URL err = new URL(location);
        BufferedReader br = new BufferedReader(new InputStreamReader(err.openStream(), "UTF-8"));
        while (true) {
            String line = br.readLine();
            if (line == null) {
                break;
            }
            int pos1 = line.indexOf('=');
            if (pos1 < 0) {
                continue;
            }
            String bundleSymbolicName = line.substring(0, pos1);
            String bundleName = line.substring(pos1 + 1);
            for (BundleInfo info : list) {
                if (info.bundleSymbolicName.equals(bundleSymbolicName)) {
                    info.bundleName = bundleName;
                    break;
                }
            }
        }
        return true;
    } catch (IOException e) {
        e.printStackTrace();
    }
    return false;
}

public static boolean decodeFileToFile(String infile, String outfile) {
    boolean success = false;
    java.io.InputStream in = null;
    java.io.OutputStream out = null;
    try {
        in = new Base64.InputStream(new java.io.BufferedInputStream(new java.io.FileInputStream(infile)), Base64.DECODE);
        out = new java.io.BufferedOutputStream(new java.io.FileOutputStream(outfile));
        byte[] buffer = new byte[65536];
        int read = -1;
        while ((read = in.read(buffer)) >= 0) {
            out.write(buffer, 0, read);
        }
        success = true;
    } catch (java.io.IOException exc) {
        exc.printStackTrace();
    } finally {
        try {
            in.close();
        } catch (Exception exc) {
