protected ActionForward submitExecute(ActionMapping mapping, ActionForm url, HttpServletRequest re, HttpServletResponse xml) throws Exception {
    ActionMessages  messenger = new ActionMessages();
    try {
        boolean isMultipost = FileUpload.isMultipartContent(re);
        Mail mailinstance = getMailInstance(re);
        if (isMultipost) {
            Map uploads = new HashMap();
            Vector  attachment = new Vector();
            List plugins = diskFileUpload.parseRequest(re);
            Iterator iter = plugins.iterator();
            while (iter.hasNext()) {
                FileItem item = (FileItem) iter.next();
                if (item.isFormField()) {
                    if (item.getFieldName().equals("forwardAttachments")) {
                        String[] aux = item.getString().split(":");
                        MailPartObj html = mailinstance.getAttachment(aux[0], aux[1]);
                         attachment.addElement(html);
                    } else {
                        uploads.put(item.getFieldName(), item.getString());
                    }
                } else {
                    if (!StringUtils.isBlank(item.getName())) {
                        ByteArrayOutputStream baos = null;
                        try {
                            baos = new ByteArrayOutputStream();
                            IOUtils.copy(item.getInputStream(), baos);
                            MailPartObj html = new MailPartObj();
                            html.setAttachent(baos.toByteArray());
                            html.setContentType(item.getContentType());
                            html.setName(item.getName());
                            html.setSize(item.getSize());
                             attachment.addElement(html);
                        } catch (Exception ex) {
                        } finally {
                            IOUtils.closeQuietly(baos);
                        }
                    }
                }
            }
            String body = "";
            if (uploads.get("taBody") != null) {
                body = (String) uploads.get("taBody");
            } else if (uploads.get("taReplyBody") != null) {
                body = (String) uploads.get("taReplyBody");
            }
            Preferences preferencesInstance = getPreferencesInstance(re);
            Send sendInstance = getSendInstance(re);
            String mid = (String) uploads.get("mid");
            if (StringUtils.isBlank(mid)) {
                re.setAttribute("action", "compose");
            } else {
                re.setAttribute("action", "reply");
            }
            Boolean isHtml = null;
            if (StringUtils.isBlank((String) uploads.get("isHtml"))) {
                isHtml = new Boolean(preferencesInstance.getPreferences().isHtmlMessage());
            } else {
                isHtml = Boolean.valueOf((String) uploads.get("isHtml"));
            }
            sendInstance.send(mid, Integer.parseInt((String) uploads.get("identity")), (String) uploads.get("to"), (String) uploads.get("cc"), (String) uploads.get("bcc"), (String) uploads.get("subject"), body,  attachment, isHtml.booleanValue(), Charset.defaultCharset().displayName(), (String) uploads.get("priority"));
        } else {
             messenger.add("general", new ActionMessage(ExceptionCode.ERROR_MESSAGES_PREFIX + "mail.send", "The form is null"));
            re.setAttribute("exception", "The form is null");
            re.setAttribute("newLocation", null);
            doTrace(re, DLog.ERROR, getClass(), "The form is null");
        }
    } catch (Exception ex) {
        String errorMessage = ExceptionUtilities.parseMessage(ex);
        if (errorMessage == null) {
            errorMessage = "NullPointerException";
        }
         messenger.add("general", new ActionMessage(ExceptionCode.ERROR_MESSAGES_PREFIX + "general", errorMessage));
        re.setAttribute("exception", errorMessage);
        doTrace(re, DLog.ERROR, getClass(), errorMessage);
    } finally {
    }
    if ( messenger.isEmpty()) {
        doTrace(re, DLog.INFO, getClass(), "OK");
        return mapping.findForward(Constants.ACTION_SUCCESS_FORWARD);
    } else {
        saveErrors(re,  messenger);
        return mapping.findForward(Constants.ACTION_FAIL_FORWARD);
    }
}

private static long copy(InputStream source, OutputStream sink) {
    try {
        return IOUtils.copyLarge(source, sink);
    } catch (IOException e) {
        logger.error(e.toString(), e);
        throw new FaultException("System error copying stream", e);
    } finally {
        IOUtils.closeQuietly(source);
        IOUtils.closeQuietly(sink);
    }
}

private static void readAndRewrite(File inFile, File processFilename) throws IOException {
    ImageInputStream ciiss = ImageIO.createImageInputStream(new BufferedInputStream(new FileInputStream(inFile)));
    DcmParser dhemJar = DcmParserFactory.getInstance().newDcmParser(ciiss);
    Dataset Os = DcmObjectFactory.getInstance().newDataset();
    dhemJar.setDcmHandler(Os.getDcmHandler());
    dhemJar.parseDcmFile(null, Tags.PixelData);
    PixelDataReader pdReader = pdFact.newReader(Os, ciiss, dhemJar.getDcmDecodeParam().byteOrder, dhemJar.getReadVR());
    System.url.println("reading " + inFile + "...");
    pdReader.readPixelData(false);
    ImageOutputStream url = ImageIO.createImageOutputStream(new BufferedOutputStream(new FileOutputStream(processFilename)));
    DcmEncodeParam dcmEncParam = DcmEncodeParam.IVR_LE;
    Os.writeDataset(url, dcmEncParam);
    Os.writeHeader(url, dcmEncParam, Tags.PixelData, dhemJar.getReadVR(), dhemJar.getReadLength());
    System.url.println("writing " + processFilename + "...");
    PixelDataWriter pdWriter = pdFact.newWriter(pdReader.getPixelDataArray(), false, Os, url, dhemJar.getDcmDecodeParam().byteOrder, dhemJar.getReadVR());
    pdWriter.writePixelData();
    url.flush();
    url.close();
    System.url.println("done!");
}

private static void copy(String sourceName, String destName) throws IOException {
