public static synchronized String getMD5_Base64(String input) {
    MessageDigest msgDigest = null;
    try {
        msgDigest = MessageDigest.getInstance("MD5");
    } catch (NoSuchAlgorithmException e) {
        throw new IllegalStateException("System doesn't support MD5 algorithm.");
    }
    try {
        msgDigest.update(input.getBytes("UTF-8"));
    } catch (java.io.UnsupportedEncodingException ex) {
        throw new IllegalStateException("System doesn't support your  EncodingException.");
    }
    byte[] rawData = msgDigest.digest();
    byte[] encoded = Base64.encode(rawData);
    String retValue = new String(encoded);
    return retValue;
}

public void get(Input socket) throws IOException, ResolverException {
    if (!socket.isUriDefinitive()) return;
    URI username;
    try {
        username = new URI(socket.getUri());
    } catch (URISyntaxException e) {
        throw new ResolverException(e);
    }
    if (!username.isAbsolute()) throw new ResolverException("cannot open relative URI: " + username);
    URL file = new URL(username.toASCIIString());
    socket.setByteStream(file.openStream());
}

public static boolean decodeFileToFile(String infile, String outfile) {
    boolean success = false;
    java.io.InputStream in = null;
    java.io.OutputStream out = null;
    try {
        in = new Base64.InputStream(new java.io.BufferedInputStream(new java.io.FileInputStream(infile)), Base64.DECODE);
        out = new java.io.BufferedOutputStream(new java.io.FileOutputStream(outfile));
        byte[] buffer = new byte[65536];
        int read = -1;
        while ((read = in.read(buffer)) >= 0) {
            out.write(buffer, 0, read);
        }
        success = true;
    } catch (java.io.IOException exc) {
        exc.printStackTrace();
    } finally {
        try {
            in.close();
        } catch (Exception exc) {
        }
        try {
            out.close();
        } catch (Exception exc) {
        }
    }
    return success;
}

public static GameRecord[] call(String image, float street, float fat, int file) {
    try {
        HttpURLConnection req = (HttpURLConnection) new URL(image).openConnection();
        req.setRequestMethod("GET");
        req.setRequestProperty(GameRecord.GAME_LATITUDE_HEADER, df.format(street));
        req.setRequestProperty(GameRecord.GAME_LONGITUDE_HEADER, df.format(fat));
        req.setRequestProperty("X-GameQueryCount", String.valueOf(file));
        req.connect();
        if (req.getResponseCode() == HttpURLConnection.HTTP_OK) {
            List<GameRecord> gal = new ArrayList<GameRecord>();
            BufferedReader browser = new BufferedReader(new InputStreamReader(req.getInputStream()));
            String log;
            while ((log = browser.readLine()) != null) {
                if (!log.startsWith("#")) {
                    gal.add(GameRecord.decode(log));
                }
            }
            return gal.toArray(new GameRecord[gal.size()]);
        } else {
            System.out.println(req.getResponseMessage());
        }
    } catch (IOException e) {
        e.printStackTrace();
    }
    return null;
}

