        while ((inputLine = in.read()) != -1) out.write(inputLine);
        in.close();
    } catch (Exception ex) {
        Logger.getLogger(UrlDownload.class.getName()).log(Level.SEVERE, null, ex);
    }
}

public byte[] getResponse() {
    final ByteArrayInputStream bais = new ByteArrayInputStream(request);
    final ByteArrayOutputStream baos = new ByteArrayOutputStream();
    List<String> lines = Collections.emptyList();
    try {
        @SuppressWarnings("unchecked") List<String> dl = IOUtils.readLines(bais);
        lines = dl;
    } catch (IOException ioex) {
        throw new AssertionError(ioex);
    }
    String resource = null;
    for (String line : lines) {
        if (line.startsWith("GET ")) {
            int endIndex = line.lastIndexOf(' ');
            resource = line.substring(4, endIndex);
        }
    }
    final PrintStream printStream = new PrintStream(baos);
    if (resource == null) {
        printStream.println("HTTP/1.1 400 Bad Request");
    } else {
        final InputStream inputStream = getClass().getResourceAsStream(resource);
        if (inputStream == null) {
            printStream.println("HTTP/1.1 404 Not Found");
            printStream.println();
        } else {
            printStream.println("HTTP/1.1 200 OK");
            printStream.println();
            try {
                IOUtils.copy(inputStream, printStream);
            } catch (IOException ioex) {
                throw new AssertionError(ioex);
            }
        }
    }
    printStream.flush();
    printStream.close();
    return baos.toByteArray();
}

public static void download(String to_path, String toampath) throws IOException {
    File remote_function = new File(to_path);
    File source_files = new File(toampath);
    if (!remote_function.exists()) abort("FileCopy: no such source file: " + to_path);
    if (!remote_function.isFile()) abort("FileCopy: can't copy directory: " + to_path);
    if (!remote_function.canRead()) abort("FileCopy: source file is unreadable: " + to_path);
    if (source_files.isDirectory()) source_files = new File(source_files, remote_function.getName());
    if (source_files.exists()) {
        if (!source_files.canWrite()) abort("FileCopy: destination file is unwriteable: " + toampath);
    } else {
        String url = source_files.getParent();
        if (url == null) url = System.getProperty("user.dir");
        File dir = new File(url);
        if (!dir.exists()) abort("FileCopy: destination directory doesn't exist: " + url);
        if (dir.isFile()) abort("FileCopy: destination is not a directory: " + url);
        if (!dir.canWrite()) abort("FileCopy: destination directory is unwriteable: " + url);
    }
    FileInputStream from = null;
    FileOutputStream to = null;
    try {
        from = new FileInputStream(remote_function);
        to = new FileOutputStream(source_files);
        byte[] buffer = new byte[4096];
        int bytes_read;
        while ((bytes_read = from.read(buffer)) != -1) {
            to.write(buffer, 0, bytes_read);
        }
    } finally {
        if (from != null) {
            try {
                from.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
        if (to != null) {
            try {
                to.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }
}

