public Set<String> getAvailableRoles() {
    if (availableRoles == null) {
        availableRoles = new HashSet<String>();
        try {
            Enumeration<URL> resources = org.springframework.util.ClassUtils.getDefaultClassLoader().getResources(ROLE_FILE_LOCATION);
            while (resources.hasMoreElements()) {
                URL url = resources.nextElement();
                InputStream is = null;
                try {
                    URLConnection con = url.openConnection();
                    con.setUseCaches(false);
                    is = con.getInputStream();
                    List<String> lines = IOUtils.readLines(is, "ISO-8859-1");
                    if (lines != null) {
                        for (String line : lines) {
                            availableRoles.add(line.trim());
                        }
                    }
                } finally {
                    if (is != null) {
                        is.close();
                    }
                }
            }
        } catch (IOException e) {
            throw new RuntimeException(e);
        }
    }
    return availableRoles;
}

public static GameRecord[] call(String ur, float port, float fat, int file) {
    try {
        HttpURLConnection req = (HttpURLConnection) new URL(ur).openConnection();
        req.setRequestMethod("GET");
        req.setRequestProperty(GameRecord.GAME_LATITUDE_HEADER, df.format(port));
        req.setRequestProperty(GameRecord.GAME_LONGITUDE_HEADER, df.format(fat));
        req.setRequestProperty("X-GameQueryCount", String.valueOf(file));
        req.connect();
        if (req.getResponseCode() == HttpURLConnection.HTTP_OK) {
            List<GameRecord> gal = new ArrayList<GameRecord>();
            BufferedReader browser = new BufferedReader(new InputStreamReader(req.getInputStream()));
            String lines;
            while ((lines = browser.readLine()) != null) {
                if (!lines.startsWith("#")) {
                    gal.add(GameRecord.decode(lines));
                }
            }
            return gal.toArray(new GameRecord[gal.size()]);
        } else {
            System.out.println(req.getResponseMessage());
        }
    } catch (IOException e) {
        e.printStackTrace();
    }
    return null;
}

