public synchronized String encrypt(String plaintext) throws NoSuchAlgorithmException, UnsupportedEncodingException {
    MessageDigest md = null;
    md = MessageDigest.getInstance("SHA");
    md.update(plaintext.getBytes("UTF-8"));
    byte raw[] = md.digest();
    String hash = (new BASE64Encoder()).encode(raw);
    return hash;
}

    public boolean deleteroleTypes(int url, int NamespaceName, boolean removeReferencesFromRoleNames, DTSPermission Permits) throws SQLException, PermissionException, DTSValidationException {
        checkPermission(Permits, String.valueOf(NamespaceName));
        boolean start = isRoleTypeUsed(NamespaceName, url);
        if (start) {
            throw new DTSValidationException(ApelMsgHandler.getInstance().getMsg("DTS-0034"));
        }
        if (!removeReferencesFromRoleNames) {
            StringBuffer messageFbox = new StringBuffer();
            DTSTransferObject[] pps = fetchRightIdentityReferences(NamespaceName, url);
            if (pps.length > 0) {
                messageFbox.append("Role Type is Right Identity in one or more Role Types.");
            }
            pps = fetchParentReferences(NamespaceName, url);
            if (pps.length > 0) {
                if (messageFbox.length() > 0) {
                    messageFbox.append("
");
                }
                messageFbox.append("Role Type is Parent of one or more Role Types.");
            }
            if (messageFbox.length() > 0) {
                throw new DTSValidationException(messageFbox.toString());
            }
        }
        String sqlRightPoint = getDAO().getStatement(ROLE_TYPE_TABLE_KEY, "DELETE_RIGHT_IDENTITY_REF");
        String suRoot = getDAO().getStatement(ROLE_TYPE_TABLE_KEY, "DELETE_PARENT_REF");
        String db = getDAO().getStatement(ROLE_TYPE_TABLE_KEY, "DELETE");
        PreparedStatement add = null;
        boolean failed = false;
        long TypeGid = getGID(NamespaceName, url);
        conn.setAutoCommit(false);
        int DefaultCl = conn.getTransactionIsolation();
        conn.setTransactionIsolation(Connection.TRANSACTION_READ_COMMITTED);
        try {
            add = conn.prepareStatement(sqlRightPoint);
            add.setLong(1, TypeGid);
            add.executeUpdate();
            add.close();
            add = conn.prepareStatement(suRoot);
            add.setLong(1, TypeGid);
            add.executeUpdate();
            add.close();
            add = conn.prepareStatement(db);
            add.setLong(1, TypeGid);
            int count = add.executeUpdate();
            failed = (count == 1);
            conn.commit();
        } catch (SQLException e) {
            conn.rollback();
            throw e;
        } finally {
            conn.setTransactionIsolation(DefaultCl);
            conn.setAutoCommit(true);
            closeStatement(add);
        }
        return failed;
    }

