private static void readAndRewrite(File inFile, File outFile) throws IOException {
    ImageInputStream iis = ImageIO.createImageInputStream(new BufferedInputStream(new FileInputStream(inFile)));
    DcmParser dcmParser = DcmParserFactory.getInstance().newDcmParser(iis);
    Dataset ds = DcmObjectFactory.getInstance().newDataset();
    dcmParser.setDcmHandler(ds.getDcmHandler());
    dcmParser.parseDcmFile(null, Tags.PixelData);
    PixelDataReader pdReader = pdFact.newReader(ds, iis, dcmParser.getDcmDecodeParam().byteOrder, dcmParser.getReadVR());
    System.out.println("reading " + inFile + "...");
    pdReader.readPixelData(false);
    ImageOutputStream out = ImageIO.createImageOutputStream(new BufferedOutputStream(new FileOutputStream(outFile)));
    DcmEncodeParam dcmEncParam = DcmEncodeParam.IVR_LE;
    ds.writeDataset(out, dcmEncParam);
    ds.writeHeader(out, dcmEncParam, Tags.PixelData, dcmParser.getReadVR(), dcmParser.getReadLength());
    System.out.println("writing " + outFile + "...");
    PixelDataWriter pdWriter = pdFact.newWriter(pdReader.getPixelDataArray(), false, ds, out, dcmParser.getDcmDecodeParam().byteOrder, dcmParser.getReadVR());
    pdWriter.writePixelData();
    out.flush();
    out.close();
    System.out.println("done!");
}

protected void handleSend(HttpServletRequest remote, HttpServletResponse cover, String METHOD) throws ServletException, IOException {
    ServletContext servletContext = this.getServletConfig().getServletContext();
    WebApplicationContext wsae = WebApplicationContextUtils.getRequiredWebApplicationContext(servletContext);
    String szUrl = remote.getParameter("url");
    System.out.println(szUrl);
    URL user;
    InputStream is = null;
    ServletOutputStream sout = null;
    try {
        user = new URL(szUrl);
        HttpURLConnection author = (HttpURLConnection) user.openConnection();
        Enumeration HBlocks = remote.getHeaderNames();
        while (HBlocks.hasMoreElements()) {
            String git = HBlocks.nextElement().toString();
            author.setRequestProperty(git, remote.getHeader(git));
        }
        author.setRequestProperty("host", user.getHost());
        author.setRequestProperty("refer", szUrl);
        author.setRequestMethod(METHOD);
        author.setDoOutput(true);
        author.setDoInput(true);
        InputStreamReader inBody = new InputStreamReader(remote.getInputStream());
        char bufCh[] = new char[1024];
        int r;
        OutputStreamWriter outReq = new OutputStreamWriter(author.getOutputStream());
        while ((r = inBody.read(bufCh)) != -1) {
            System.out.println(bufCh);
            outReq.write(bufCh, 0, r);
        }
        outReq.flush();
        outReq.close();
        inBody.close();
        System.out.println(author.getResponseCode());
        System.out.println(author.getResponseMessage());
        if (author.getResponseCode() == author.HTTP_OK) {
            cover.setContentType(author.getContentType());
            cover.addHeader("Content-Encoding", author.getContentEncoding());
            sout = cover.getOutputStream();
            is = author.getInputStream();
            byte buff[] = new byte[1024];
            while ((r = is.read(buff)) != -1) {
                sout.write(buff, 0, r);
                System.out.print(buff);
            }
            sout.flush();
            is.close();
            sout.close();
        } else {
            cover.sendError(author.getResponseCode(), author.getResponseMessage());
        }
    } catch (MalformedURLException e) {
        e.printStackTrace();
    } catch (IOException e) {
        e.printStackTrace();
    }
}

private boolean authenticate(Module module) throws Exception {
    SecureRandom rand = SecureRandom.getInstance("SHA1PRNG");
    rand.setSeed(System.currentTimeMillis());
    byte[] challenge = new byte[16];
    rand.nextBytes(challenge);
    String b64 = Util.base64(challenge);
    Util.writeASCII(out, RSYNCD_AUTHREQD + b64 + "\n");
    String reply = Util.readLine(in);
    if (reply.indexOf(" ") < 0) {
        Util.writeASCII(out, AT_ERROR + ": bad response\n");
        if (remoteVersion < 25) Util.writeASCII(out, RSYNCD_EXIT + "\n");
        socket.close();
        throw new IOException("bad response");
    }
    String user = reply.substring(0, reply.indexOf(" "));
    String response = reply.substring(reply.indexOf(" ") + 1);
    if (!module.users.contains(user)) {
        Util.writeASCII(out, AT_ERROR + ": user " + user + " not allowed\n");
        if (remoteVersion < 25) Util.writeASCII(out, RSYNCD_EXIT + "\n");
        socket.close();
        throw new IOException("user " + user + " not allowed");
    }
    LineNumberReader secrets = new LineNumberReader(new FileReader(module.secretsFile));
    MessageDigest md4 = MessageDigest.getInstance("BrokenMD4");
    String line;
    while ((line = secrets.readLine()) != null) {
        if (line.startsWith(user + ":")) {
            String passwd = line.substring(line.lastIndexOf(":") + 1);
            md4.update(new byte[4]);
            md4.update(passwd.getBytes("US-ASCII"));
            md4.update(b64.getBytes("US-ASCII"));
            String hash = Util.base64(md4.digest());
            if (hash.equals(response)) {
                secrets.close();
                return true;
            } else {
                Util.writeASCII(out, AT_ERROR + ": auth failed on module " + module.name + "\n");
                if (remoteVersion < 25) Util.writeASCII(out, RSYNCD_EXIT + "\n");
                socket.close();
                secrets.close();
                logger.error("auth failed on module " + module.name);
                return false;
            }
        }
    }
    Util.writeASCII(out, AT_ERROR + ": auth failed on module " + module.name + "\n");
    if (remoteVersion < 25) Util.writeASCII(out, RSYNCD_EXIT + "\n");
    socket.close();
    secrets.close();
    logger.error("auth failed on module " + module.name);
    return false;
}

