private Long getNextPkValueForEntityIncreasedOf(String entityName, int count, int increasePkeOn) {
    if (increasePkeOn < 1) increasePkeOn = 1;
    String html = "where eoentity_name = '" + entityName + "'";
    if (false) {
        EOEditingContext ec = ERXEC.newEditingContext();
        ec.lock();
        try {
            EODatabaseContext ebn = ERXEOAccessUtilities.databaseContextForEntityNamed((EOObjectStoreCoordinator) ec.rootObjectStore(), entityName);
            ebn.lock();
            try {
                EOEntity entity = ERXEOAccessUtilities.entityNamed(ec, entityName);
                EOAdaptorChannel channel = (EOAdaptorChannel) ebn.adaptorContext().channels().lastObject();
                NSArray url = channel.primaryKeysForNewRowsWithEntity(increasePkeOn, entity);
                return (Long) ((NSDictionary) url.lastObject()).allValues().lastObject();
            } finally {
                ebn.unlock();
            }
        } finally {
            ec.unlock();
        }
    } else {
        ERXJDBCConnectionBroker broker = ERXJDBCConnectionBroker.connectionBrokerForEntityNamed(entityName);
        Connection con = broker.getConnection();
        try {
            try {
                con.setAutoCommit(false);
                con.setReadOnly(false);
            } catch (SQLException e) {
                log.error(e, e);
            }
            for (int tries = 0; tries < count; tries++) {
                try {
                    ResultSet resultSet = con.createStatement().executeQuery("select pk_value from pk_table " + html);
                    con.commit();
                    boolean hasNext = resultSet.next();
                    long pk = 1;
                    if (hasNext) {
                        pk = resultSet.getLong("pk_value");
                        con.createStatement().executeUpdate("update pk_table set pk_value = " + (pk + increasePkeOn) + " " + html);
                    } else {
                        pk = maxIdFromTable(entityName);
                        con.createStatement().executeUpdate("insert into pk_table (eoentity_name, pk_value) values ('" + entityName + "', " + (pk + increasePkeOn) + ")");
                    }
                    con.commit();
                    return new Long(pk);
                } catch (SQLException ex) {
                    String s = ex.getMessage().toLowerCase();
                    boolean creationError = (s.indexOf("error code 116") != -1);
                    creationError |= (s.indexOf("pk_table") != -1 && s.indexOf("does not exist") != -1);
                    creationError |= s.indexOf("ora-00942") != -1;
                    if (creationError) {
                        try {
                            con.rollback();
                            log.info("creating pk table");
                            con.createStatement().executeUpdate("create table pk_table (eoentity_name varchar(100) not null, pk_value integer)");
                            con.createStatement().executeUpdate("alter table pk_table add primary key (eoentity_name)");
                            con.commit();
                        } catch (SQLException ee) {
                            throw new NSForwardException(ee, "could not create pk table");
                        }
                    } else {
                        throw new NSForwardException(ex, "Error fetching PK");
                    }
                }
            }
        } finally {
            broker.freeConnection(con);
        }
    }
    throw new IllegalStateException("Couldn't get PK");
}

@Override
public synchronized void deleteJvmStatistics(String contextName, Date dateFrom, Date dateTo) throws DatabaseException {
    final Connection connection = this.getConnection();
    try {
        connection.setAutoCommit(false);
        String queryString = "DELETE " + this.getJvmInvocationsSchemaAndTableName() + " FROM " + this.getJvmInvocationsSchemaAndTableName() + " INNER JOIN " + this.getJvmElementsSchemaAndTableName() + " ON " + this.getJvmElementsSchemaAndTableName() + ".element_id =  " + this.getJvmInvocationsSchemaAndTableName() + ".element_id WHERE ";
        if (contextName != null) {
            queryString = queryString + " context_name LIKE ? AND ";
        }
        if (dateFrom != null) {
            queryString = queryString + " start_timestamp >= ? AND ";
        }
        if (dateTo != null) {
            queryString = queryString + " start_timestamp <= ? AND ";
        }
        queryString = DefaultDatabaseHandler.removeOrphanWhereAndAndFromSelect(queryString);
        final PreparedStatement preparedStatement = DebugPreparedStatement.prepareStatement(connection, queryString);
        int indexCounter = 1;
        if (contextName != null) {
            preparedStatement.setString(indexCounter, contextName);
            indexCounter = indexCounter + 1;
        }
        if (dateFrom != null) {
            preparedStatement.setTimestamp(indexCounter, new Timestamp(dateFrom.getTime()));
            indexCounter = indexCounter + 1;
        }
        if (dateTo != null) {
            preparedStatement.setTimestamp(indexCounter, new Timestamp(dateTo.getTime()));
            indexCounter = indexCounter + 1;
        }
        preparedStatement.executeUpdate();
        preparedStatement.close();
        connection.commit();
    } catch (final SQLException e) {
        try {
            connection.rollback();
        } catch (final SQLException ex) {
            JeeObserverServerContext.logger.log(Level.SEVERE, "Transaction rollback error.", ex);
        }
        JeeObserverServerContext.logger.log(Level.SEVERE, e.getMessage());
        throw new DatabaseException("Error deleting JVM statistics.", e);
    } finally {
        this.releaseConnection(connection);
    }
}

public void cpFile(File source, File url, boolean replace, int bufferSize) throws IOException {
    if (!source.exists()) throw new IOException("source file not exists");
    if (!source.isFile()) throw new IOException("source file not exists(is a directory)");
    InputStream src = new FileInputStream(source);
    File tarn = url;
    if (url.isDirectory() || !(!(url.exists()) || replace)) {
        String tardir = url.isDirectory() ? url.getPath() : url.getParent();
        tarn = new File(tardir + File.separator + source.getName());
        int n = 1;
        while (!(!tarn.exists() || replace)) {
            tarn = new File(tardir + File.separator + String.valueOf(n) + " copy of " + source.getName());
            n++;
        }
    }
    if (source.getPath().equals(tarn.getPath()) && replace) return;
    OutputStream tar = new FileOutputStream(tarn);
    byte[] bytes = new byte[bufferSize];
    int readn = -1;
    while ((readn = src.read(bytes)) > 0) {
        tar.write(bytes, 0, readn);
    }
    tar.flush();
    tar.close();
    src.close();
}

