public static void DecodeMapFile(String mapFile, String outputFile) throws Exception {
    byte magicKey = 0;
    byte[] buffer = new byte[2048];
    int nread;
    InputStream map;
    OutputStream output;
    try {
        map = new FileInputStream(mapFile);
    } catch (Exception e) {
        throw new Exception("Map file error", e);
    }
    try {
        output = new FileOutputStream(outputFile);
    } catch (Exception e) {
        throw new Exception("Map file error", e);
    }
    while ((nread = map.read(buffer, 0, 2048)) != 0) {
        for (int i = 0; i < nread; ++i) {
            buffer[i] ^= magicKey;
            magicKey += 43;
        }
        output.write(buffer, 0, nread);
    }
    map.close();
    output.close();
}

public static void transferDir(File filename, File url, int bufSize, boolean force) throws IOException {
    logger.info("copyFile(File src=" + filename + ", File dest=" + url + ", int bufSize=" + bufSize + ", boolean force=" + force + ") - start");
    File f = new File(Configuration.getArchiveDir());
    if (!f.exists()) {
        f.mkdir();
    }
    if (url.exists()) {
        if (force) {
            url.delete();
        } else {
            throw new IOException("Cannot overwrite existing file: " + url);
        }
    }
    byte[] buffer = new byte[bufSize];
    int read = 0;
    InputStream con = null;
    OutputStream connection = null;
    try {
        con = new FileInputStream(filename);
        connection = new FileOutputStream(url);
        while (true) {
            read = con.read(buffer);
            if (read == -1) {
                break;
            }
            connection.write(buffer, 0, read);
        }
    } finally {
        if (con != null) {
            try {
                con.close();
            } finally {
                if (connection != null) {
                    connection.close();
                }
            }
        }
    }
    logger.debug("copyFile(File, File, int, boolean) - end");
}

