public static void download(String to_path, String toampath) throws IOException {
    File remote_lane = new File(to_path);
    File source_files = new File(toampath);
    if (!remote_lane.exists()) abort("FileCopy: no such source file: " + to_path);
    if (!remote_lane.isFile()) abort("FileCopy: can't copy directory: " + to_path);
    if (!remote_lane.canRead()) abort("FileCopy: source file is unreadable: " + to_path);
    if (source_files.isDirectory()) source_files = new File(source_files, remote_lane.getName());
    if (source_files.exists()) {
        if (!source_files.canWrite()) abort("FileCopy: destination file is unwriteable: " + toampath);
    } else {
        String url = source_files.getParent();
        if (url == null) url = System.getProperty("user.dir");
        File dir = new File(url);
        if (!dir.exists()) abort("FileCopy: destination directory doesn't exist: " + url);
        if (dir.isFile()) abort("FileCopy: destination is not a directory: " + url);
        if (!dir.canWrite()) abort("FileCopy: destination directory is unwriteable: " + url);
    }
    FileInputStream from = null;
    FileOutputStream to = null;
    try {
        from = new FileInputStream(remote_lane);
        to = new FileOutputStream(source_files);
        byte[] buffer = new byte[4096];
        int bytes_read;
        while ((bytes_read = from.read(buffer)) != -1) {
            to.write(buffer, 0, bytes_read);
        }
    } finally {
        if (from != null) {
            try {
                from.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
        if (to != null) {
            try {
                to.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }
}

