private void copyfile(final String updateFile, final String destinationFILE) throws IOException {
    HttpClient method = new DefaultHttpClient();
    HttpGet httpGet = new HttpGet(updateFile);
    final File outFile = new File(destinationFILE);
    createParentDirectories(outFile);
    FileOutputStream inputFile;
    inputFile = new FileOutputStream(outFile);
    final HttpResponse version = method.execute(httpGet);
    if (isInterrupted()) {
        inputFile.close();
        return;
    }
    final HttpEntity entity = version.getEntity();
    InputStream inputStream = null;
    try {
        if (entity != null) {
            inputStream = entity.getContent();
            CopyStreamStatusCallback back = new CopyStreamStatusCallback() {

                @Override
                public long getSkipBetweenUpdates() {
                    return entity.getContentLength() * 2 / PERCENTAGE_BASE;
                }

                @Override
                public void onUpdate(final long copixedBytes) {
                    int  Percentages = (int) (copixedBytes * PERCENTAGE_BASE / entity.getContentLength());
                    handleUpdate(STATUS_DOWNLOADING,  Percentages);
                }
            };
            copyStreams(inputStream, inputFile, back);
        }
    } finally {
        try {
            inputFile.close();
            if (inputStream != null) {
                inputStream.close();
            }
        } catch (IOException e) {
            Log.v(DictionaryForMIDs.LOG_TAG, "Exception while closing stream: " + e);
        }
    }
}

public static void copyFileChannel(File src, File dst, boolean preserveModificationTime) throws IOException {
    FileChannel inputChannel = null;
    FileChannel outputChannel = null;
    long length = 0;
    try {
        inputChannel = new FileInputStream(src).getChannel();
        length = inputChannel.size();
        outputChannel = new FileOutputStream(dst).getChannel();
        long total = 0;
        while (total < length) {
            total += inputChannel.transferTo(0, length, outputChannel);
        }
        if (preserveModificationTime) {
            dst.setLastModified(src.lastModified());
        }
    } finally {
        if (inputChannel != null) {
            inputChannel.close();
        }
        if (outputChannel != null) {
            outputChannel.close();
        }
    }
}

