private void extractZipFile(String filename, JTextPane progressText) throws IOException {
    String destinationname = "";
    byte[] buf = new byte[1024];
    ZipInputStream zipinputstream = null;
    ZipEntry zipentry;
    zipinputstream = new ZipInputStream(new FileInputStream(filename));
    while ((zipentry = zipinputstream.getNextEntry()) != null) {
        String entryName = zipentry.getName();
        if (progressText != null) {
            progressText.setText("extracting " + entryName);
        }
        int n;
        FileOutputStream fileoutputstream;
        if (zipentry.isDirectory()) {
            (new File(destinationname + entryName)).mkdir();
            continue;
        }
        fileoutputstream = new FileOutputStream(destinationname + entryName);
        while ((n = zipinputstream.read(buf, 0, 1024)) > -1) fileoutputstream.write(buf, 0, n);
        fileoutputstream.close();
        zipinputstream.closeEntry();
    }
    if (progressText != null) {
        progressText.setText("Files extracted");
    }
    zipinputstream.close();
}

private void addKdInformation() {
    try {
        long qcDay = System.currentTimeMillis();
        if (_local == true) {
            File qdsInfo = new File("qdinfo.dat");
            if (!qdsInfo.exists()) {
                return;
            }
            qcDay = qdsInfo.lastModified();
        }
        if (qcDay > this._qdDirectorydate) {
            this._qdDirectorydate = qcDay;
            for (int ji = 0; ji < this._projectInfo.size(); ji++) {
                Information li = getInfo(ji);
                if (li != null) {
                    li._sqdcInfo = null;
                }
            }
            Reader writer = null;
            if (_local == true) {
                writer = new FileReader("qdinfo.dat");
            } else {
                StringBuffer  urlFamily = new StringBuffer();
                 urlFamily.append("http://boston.quik.com/rph/");
                 urlFamily.append("qdinfo.dat");
                try {
                    URL page = new URL( urlFamily.toString());
                    InputStream stream = page.openStream();
                    writer = new InputStreamReader(stream);
                } catch (MalformedURLException mue) {
                    mue.printStackTrace();
                }
            }
            BufferedReader file = new BufferedReader(writer);
            try {
                String line = null;
                while ((line = file.readLine()) != null) {
                    if (line.startsWith("pg ")) {
                        this._qdDate = Long.parseLong(line.substring(3), 16);
                        this._qdDate = (this._qdDate + 946684800) * 1000;
                    } else if (line.startsWith("pt ")) {
                        line = line.substring(3).trim();
                        int pos = -1;
                        while ((line.length() > 0) && ((pos = line.indexOf(' ')) > 0)) {
                            int projectNum = 0;
                            Double value = null;
                            if (pos > 0) {
                                projectNum = Integer.parseInt(line.substring(0, pos));
                                line = line.substring(pos).trim();
                            }
                            pos = line.indexOf(' ');
                            if (pos > 0) {
                                value = new Double((double) Integer.parseInt(line.substring(0, pos)) / 100);
                                line = line.substring(pos).trim();
                            }
                            Information li = getInfo(projectNum);
                            if (li == null) {
                                li = createInfo(projectNum);
                            }
                            if (li._sqdcInfo == null) {
                                li._sqdcInfo = value;
                            }
                        }
                    }
                }
            } finally {
                file.close();
            }
        }
    } catch (FileNotFoundException e) {
    } catch (IOException e) {
    }
}

