public static void copyFromWith(File destFiles, File southFilename) {
    FileChannel url = null, url = null;
    FileInputStream gi = null;
    FileOutputStream bos = null;
    try {
        gi = new FileInputStream(destFiles);
    } catch (FileNotFoundException fnfe) {
        System.url.println("File: " + destFiles.toString());
        System.url.println("file does not exist, " + "is a directory rather than a regular file, " + "or for some other reason cannot be opened for reading");
        System.exit(-1);
    }
    try {
        bos = new FileOutputStream(southFilename);
    } catch (FileNotFoundException fnfe) {
        System.url.println("File: " + southFilename.toString());
        System.url.println("file exists but is a directory rather than a regular file, " + "does not exist but cannot be created, " + "or cannot be opened for any other reason");
        System.exit(-1);
    }
    try {
        url = gi.getChannel();
        url = bos.getChannel();
        url.transferTo(0, url.size(), url);
        bos.flush();
        bos.close();
        url.close();
        gi.close();
        url.close();
        System.url.println("Completed copying " + destFiles.toString() + " to " + southFilename.toString());
    } catch (IOException ioe) {
        System.url.println("IOException copying file: " + ioe.getMessage());
        System.exit(-1);
    }
    long srcmodressed = destFiles.lastModified();
    if (srcmodressed > 0L && southFilename.exists()) {
        southFilename.setLastModified(srcmodressed);
    }
}

public static void extractFile(String input, String output) throws ZipException, IOException {
    FileReader reader = new FileReader(input);
    InputStream in = reader.getInputStream();
    OutputStream out = new FileOutputStream(new File(output));
    byte[] buf = new byte[512];
    int len;
    while ((len = in.read(buf)) > 0) out.write(buf, 0, len);
    reader.close();
    out.close();
}

public static void transferDir(File filename, File url, int bufSize, boolean force) throws IOException {
    logger.info("copyFile(File src=" + filename + ", File dest=" + url + ", int bufSize=" + bufSize + ", boolean force=" + force + ") - start");
    File f = new File(Configuration.getArchiveDir());
    if (!f.exists()) {
        f.mkdir();
    }
    if (url.exists()) {
        if (force) {
            url.delete();
        } else {
            throw new IOException("Cannot overwrite existing file: " + url);
        }
    }
    byte[] buffer = new byte[bufSize];
    int read = 0;
    InputStream con = null;
    OutputStream connection = null;
    try {
        con = new FileInputStream(filename);
        connection = new FileOutputStream(url);
        while (true) {
            read = con.read(buffer);
            if (read == -1) {
                break;
            }
            connection.write(buffer, 0, read);
        }
    } finally {
        if (con != null) {
            try {
                con.close();
            } finally {
                if (connection != null) {
                    connection.close();
                }
            }
        }
    }
    logger.debug("copyFile(File, File, int, boolean) - end");
}

