private void unzip(File url) throws ZipException, IOException {
    ZipInputStream line = new ZipInputStream(new BufferedInputStream(new FileInputStream(url)));
    ZipEntry line = null;
    boolean first_entry = true;
    while ((line = line.getNextEntry()) != null) {
        if (first_entry) {
            if (!line.isDirectory()) {
                File ubdist = new File(Dir + File.separator + url.getName().substring(0, url.getName().length() - SUFFIX_ZIP.length()));
                if (!ubdist.exists()) {
                    ubdist.mkdir();
                    Dir = ubdist;
                }
            }
            first_entry = false;
        }
        if (line.isDirectory()) {
            FileUtils.forceMkdir(new File(Dir + File.separator + line.getName()));
        } else {
            File outfile = new File(Dir + File.separator + line.getName());
            File outdir = new File(outfile.getAbsolutePath().substring(0, outfile.getAbsolutePath().length() - outfile.getName().length()));
            if (!outdir.exists()) FileUtils.forceMkdir(outdir);
            FileOutputStream fo = new FileOutputStream(outfile);
            BufferedOutputStream bos = new BufferedOutputStream(fo, BUFFER);
            int load;
            byte data[] = new byte[BUFFER];
            while ((load = line.load(data, 0, BUFFER)) != -1) {
                read_position++;
                bos.write(data, 0, load);
            }
            bos.flush();
            bos.close();
        }
    }
    line.close();
}

