    BufferedOutputStream bos = new BufferedOutputStream(out, 2048);
    try {
        IOUtils.copy(streamToLoad, bos);
    } finally {
        IOUtils.closeQuietly(streamToLoad);
        IOUtils.closeQuietly(bos);
    }
    getCargo().put(GWT_ENTRY_POINT_PAGE_PARAM, null);
}

