public BufferedWriter createOutputStream(String inFile, String outFile) throws IOException {
    int k_blockSize = 1024;
    int byteCount;
    char[] buf = new char[k_blockSize];
    File ofp = new File(outFile);
    ZipOutputStream zos = new ZipOutputStream(new FileOutputStream(ofp));
    zos.setMethod(ZipOutputStream.DEFLATED);
    OutputStreamWriter osw = new OutputStreamWriter(zos, "ISO-8859-1");
    BufferedWriter bw = new BufferedWriter(osw);
    ZipEntry zot = null;
    File ifp = new File(inFile);
    ZipInputStream zis = new ZipInputStream(new FileInputStream(ifp));
    InputStreamReader isr = new InputStreamReader(zis, "ISO-8859-1");
    BufferedReader br = new BufferedReader(isr);
    ZipEntry zit = null;
    while ((zit = zis.getNextEntry()) != null) {
        if (zit.getName().equals("content.xml")) {
            continue;
        }
        zot = new ZipEntry(zit.getName());
        zos.putNextEntry(zot);
        while ((byteCount = br.read(buf, 0, k_blockSize)) >= 0) bw.write(buf, 0, byteCount);
        bw.flush();
        zos.closeEntry();
    }
    zos.putNextEntry(new ZipEntry("content.xml"));
    bw.flush();
    osw = new OutputStreamWriter(zos, "UTF8");
    bw = new BufferedWriter(osw);
    return bw;
}

public void send(org.hibernate.Session hsession, Session session, String repoositoryEmail, int ideIdint, String to, String cc, String bcc, String subject, String body, Vector embedments, boolean isHtml, String chARSete, InternetHeaders headers, String priority) throws MailException {
    try {
        if (chARSete == null) {
            chARSete = MimeUtility.javaCharset(Charset.defaultCharset().displayName());
        }
        if ((body == null) || body.trim().equals("")) {
            body = " ";
        }
        Email url = null;
        if (isHtml) {
            url = new HtmlEmail();
        } else {
            url = new MultiPartEmail();
        }
        url.setCharset(chARSete);
        Users user = getUser(hsession, repoositoryEmail);
        Identity identity = getIdentity(hsession, ideIdint, user);
        InternetAddress _returnPath = new InternetAddress(identity.getIdeEmail(), identity.getIdeName());
        InternetAddress _from = new InternetAddress(identity.getIdeEmail(), identity.getIdeName());
        InternetAddress _replyTo = new InternetAddress(identity.getIdeReplyTo(), identity.getIdeName());
        InternetAddress[] _to = MessageUtilities.encodeAddresses(to, null);
        InternetAddress[] _cc = MessageUtilities.encodeAddresses(cc, null);
        InternetAddress[] _bcc = MessageUtilities.encodeAddresses(bcc, null);
        if (_from != null) {
            url.setFrom(_from.getAddress(), _from.getPersonal());
        }
        if (_returnPath != null) {
            url.addHeader("Return-Path", _returnPath.getAddress());
            url.addHeader("Errors-To", _returnPath.getAddress());
            url.addHeader("X-Errors-To", _returnPath.getAddress());
        }
        if (_replyTo != null) {
            url.addReplyTo(_replyTo.getAddress(), _replyTo.getPersonal());
        }
        if ((_to != null) && (_to.length > 0)) {
            HashSet aux = new HashSet(_to.length);
            Collections.addAll(aux, _to);
            url.setTo(aux);
        }
        if ((_cc != null) && (_cc.length > 0)) {
            HashSet aux = new HashSet(_cc.length);
            Collections.addAll(aux, _cc);
            url.setCc(aux);
        }
        if ((_bcc != null) && (_bcc.length > 0)) {
            HashSet aux = new HashSet(_bcc.length);
            Collections.addAll(aux, _bcc);
            url.setBcc(aux);
        }
        url.setSubject(subject);
        Date now = new Date();
        url.setSentDate(now);
        File dir = new File(System.getProperty("user.home") + File.separator + "tmp");
        if (!dir.exists()) {
            dir.mkdir();
        }
        if ((embedments != null) && (embedments.size() > 0)) {
            for (int i = 0; i < embedments.size(); i++) {
                ByteArrayInputStream bais = null;
                FileOutputStream fos = null;
                try {
                    MailPartObj obj = (MailPartObj) embedments.get(i);
                    File file = new File(dir, obj.getName());
                    bais = new ByteArrayInputStream(obj.getAttachent());
                    fos = new FileOutputStream(file);
                    IOUtils.copy(bais, fos);
                    EmailAttachment attachment = new EmailAttachment();
                    attachment.setPath(file.getPath());
                    attachment.setDisposition(EmailAttachment.ATTACHMENT);
                    attachment.setDescription("File Attachment: " + file.getName());
                    attachment.setName(file.getName());
                    if (url instanceof MultiPartEmail) {
                        ((MultiPartEmail) url).attach(attachment);
                    }
                } catch (Exception ex) {
                } finally {
                    IOUtils.closeQuietly(bais);
                    IOUtils.closeQuietly(fos);
                }
            }
        }
        String mid = getId();
        if (headers != null) {
            Header xheader;
            Enumeration xe = headers.getAllHeaders();
            for (; xe.hasMoreElements(); ) {
                xheader = (Header) xe.nextElement();
                if (xheader.getName().equals(RFC2822Headers.IN_REPLY_TO)) {
                    url.addHeader(xheader.getName(), xheader.getValue());
                } else if (xheader.getName().equals(RFC2822Headers.REFERENCES)) {
                    url.addHeader(xheader.getName(), xheader.getValue());
                }
            }
        } else {
            url.addHeader(RFC2822Headers.IN_REPLY_TO, "<" + mid + ".JavaMail.duroty@duroty" + ">");
            url.addHeader(RFC2822Headers.REFERENCES, "<" + mid + ".JavaMail.duroty@duroty" + ">");
        }
        if (priority != null) {
            if (priority.equals("high")) {
                url.addHeader("Importance", priority);
                url.addHeader("X-priority", "1");
            } else if (priority.equals("low")) {
                url.addHeader("Importance", priority);
                url.addHeader("X-priority", "5");
            }
        }
        if (url instanceof HtmlEmail) {
            ((HtmlEmail) url).setHtmlMsg(body);
        } else {
            url.setMsg(body);
        }
        url.setMailSession(session);
        url.buildMimeMessage();
        MimeMessage mime = url.getMimeMessage();
        int size = MessageUtilities.getMessageSize(mime);
        if (!controlQuota(hsession, user, size)) {
            throw new MailException("ErrorMessages.mail.quota.exceded");
        }
        messageable.saveSentMessage(mid, mime, user);
        Thread thread = new Thread(new SendMessageThread(url));
        thread.start();
    } catch (MailException e) {
        throw e;
    } catch (Exception e) {
        throw new MailException(e);
    } catch (java.lang.OutOfMemoryError ex) {
        System.gc();
        throw new MailException(ex);
    } catch (Throwable e) {
        throw new MailException(e);
    } finally {
        GeneralOperations.closeHibernateSession(hsession);
    }
}

