public static String fromHtml(URL url, String defaultEncoding, boolean overrideEncoding) throws IOException, BadDocumentException {
    URLConnection conn = url.openConnection();
    String contentType = conn.getContentType();
    String encoding = conn.getContentEncoding();
    if (encoding == null) {
        int i = contentType.indexOf("charset");
        if (i >= 0) {
            String s = contentType.substring(i);
            i = s.indexOf('=');
            if (i >= 0) {
                s = contentType.substring(i + 1).trim();
                encoding = s.replace("\'", "").replace("\"", "").trim();
                if (encoding.equals("")) {
                    encoding = defaultEncoding;
                }
            }
        } else {
            encoding = defaultEncoding;
        }
    }
    String expected = "text/html";
    if (contentType == null) {
        DefaultXMLNoteErrorHandler.warning(null, 90190, "Returned content type for url.openConnection() is null");
        contentType = expected;
    }
    int index = contentType.indexOf(';');
    if (index >= 0) {
        contentType = contentType.substring(0, index).trim();
    }
    if (!contentType.equals(expected)) {
        String msg = translator.translate("The content type of url '%s' is not '%s', it is '%s'");
        throw new BadDocumentException(String.format(msg, url.toString(), expected, contentType));
    }
    BufferedReader in = new BufferedReader(new InputStreamReader(conn.getInputStream(), encoding));
    return fromHtml(in, encoding);
}

public static void download(File rubypath, File fftFile) throws IOException {
    if (rubypath.isDirectory()) {
        if (!fftFile.exists()) {
            boolean result = fftFile.mkdir();
            if (!result) throw new IOException("Unable to create directoy: " + fftFile);
        }
        String[] files = rubypath.list();
        for (String file : files) {
            download(new File(rubypath, file), new File(fftFile, file));
        }
    } else {
        if (rubypath.exists()) {
            FileChannel url = null;
            FileChannel ex = null;
            try {
                url = new FileInputStream(rubypath).getChannel();
                ex = new FileOutputStream(fftFile).getChannel();
                long size = url.size();
                MappedByteBuffer buf = url.map(FileChannel.MapMode.READ_ONLY, 0, size);
                ex.write(buf);
            } finally {
                if (url != null) url.close();
                if (ex != null) ex.close();
            }
        }
    }
}

