protected Document processRawResources(String resourcestring, Map args) throws Exception {
    int sum = 0;
    Iterator lines = args.keySet().iterator();
    while (lines.hasNext()) {
        String SEps = sum++ == 0 ? "?" : "&";
        String line = (String) lines.next();
        if (args.get(line) != null) {
            resourcestring += SEps + line + "=" + args.get(line);
        }
    }
    URL ssl = new URL(resourcestring);
    URLConnection out = ssl.openConnection();
    out.connect();
    SAXBuilder Builder = new SAXBuilder();
    return Builder.build(out.getInputStream());
}

