public static boolean encodeFileToFile(String infile, String outfile) {
    boolean success = false;
    java.io.InputStream in = null;
    java.io.OutputStream out = null;
    try {
        in = new Base64.InputStream(new java.io.BufferedInputStream(new java.io.FileInputStream(infile)), Base64.ENCODE);
        out = new java.io.BufferedOutputStream(new java.io.FileOutputStream(outfile));
        byte[] buffer = new byte[65536];
        int read = -1;
        while ((read = in.read(buffer)) >= 0) {
            out.write(buffer, 0, read);
        }
        success = true;
    } catch (java.io.IOException exc) {
        exc.printStackTrace();
    } finally {
        try {
            in.close();
        } catch (Exception exc) {
        }
        try {
            out.close();
        } catch (Exception exc) {
        }
    }
    return success;
}

public static XMLConfigurator loadFromSystemProperty(String filePart) throws IOException {
    String  urlWr = System.getProperty(filePart);
    if ( urlWr == null ||  urlWr.length() == 0) {
        return null;
    }
    InputStream isin = null;
    DOMRetriever mlUrl = null;
    try {
        URL bel = new URL( urlWr);
        mlUrl = new DOMRetriever(isin = bel.openStream());
    } catch (MalformedURLException e) {
        throw new RuntimeException(e);
    } finally {
        if (isin != null) isin.close();
    }
    return newInstance(mlUrl);
}

public String getXML(String servletURL, String request) {
    StringBuffer stringBuffer = new StringBuffer();
    try {
        String encodedRequest = URLEncoder.encode(request, "UTF-8");
        URL url = new URL(servletURL + request);
        BufferedReader in = new BufferedReader(new InputStreamReader(url.openStream()));
        String inputLine;
        while ((inputLine = in.readLine()) != null) {
            stringBuffer.append(inputLine);
        }
        in.close();
    } catch (MalformedURLException ex) {
        return null;
    } catch (UnsupportedEncodingException ex) {
        return null;
    } catch (IOException ex) {
        return null;
    }
    return stringBuffer.toString();
}

private void uri(Reader mlOut, OutputStream prefix) throws IOException, SAXException {
    Document url = null;
    try {
        DocumentBuilderFactory f = DocumentBuilderFactory.newInstance();
        f.setNamespaceAware(false);
        f.setCoalescing(true);
        f.setIgnoringComments(true);
        f.setValidating(false);
        DocumentBuilder ob = f.newDocumentBuilder();
        url = ob.parse(new InputSource(mlOut));
    } catch (ParserConfigurationException err) {
        throw new IOException(err);
    }
    Element root = url.getDocumentElement();
    if (root == null) throw new SAXException("Not root in document");
    Attr adj = root.getAttributeNode("label");
    if (adj == null) root.setAttribute("label", "Wikipedia");
    Menu parent = parseMenu(root);
    parent.key = "menuWikipedia";
    ZipOutputStream zhouter = new ZipOutputStream(prefix);
    String content = ResourceUtils.getContent(XUL4Wikipedia.class, "chrome.manifest");
    addEntry(zhouter, "chrome.manifest", content);
    content = ResourceUtils.getContent(XUL4Wikipedia.class, "install.rdf");
    addEntry(zhouter, "install.rdf", content);
    content = ResourceUtils.getContent(XUL4Wikipedia.class, "library.js");
    addDir(zhouter, "chrome/");
    addDir(zhouter, "chrome/content/");
    addDir(zhouter, "chrome/skin/");
    String signal = "/*INSERT_CMD_HERE*/";
    int n = content.indexOf(signal);
    if (n == -1) throw new RuntimeException("where is " + signal + " ??");
    ZipEntry entry = new ZipEntry("chrome/content/library.js");
    zhouter.putNextEntry(entry);
    PrintWriter pout = new PrintWriter(zhouter);
    pout.write(content.substring(0, n));
    parent.toJS(pout);
    pout.write(content.substring(n + signal.length()));
    pout.flush();
    zhouter.closeEntry();
    entry = new ZipEntry("chrome/content/menu.xul");
    zhouter.putNextEntry(entry);
    pout = new PrintWriter(zhouter);
    pout.println("<?xml version=\"1.0\" encoding=\"UTF-8\"?>");
    pout.println("<overlay id=\"xul4wikipedia\" xmlns=\"" + XUL.NS + "\">");
    pout.println("<script src=\"library.js\"/>");
    pout.println("<popup id=\"contentAreaContextMenu\">");
    pout.println("<menuseparator/>");
    parent.toXUL(pout);
    pout.println("</popup>");
    pout.println("</overlay>");
    pout.flush();
    zhouter.closeEntry();
    InputStream png = XUL4Wikipedia.class.getResourceAsStream("32px-Wikipedia-logo.png");
    if (png == null) throw new IOException("Cannot get icon");
    entry = new ZipEntry("chrome/skin/wikipedia.png");
    zhouter.putNextEntry(entry);
    IOUtils.copyTo(png, zhouter);
    zhouter.closeEntry();
    zhouter.finish();
    zhouter.flush();
}

