public void testCodingEmptyFile() throws Exception {
    ByteArrayOutputStream baos = new ByteArrayOutputStream();
    WritableByteChannel channel = newChannel(baos);
    HttpParams params = new BasicHttpParams();
    SessionOutputBuffer outbuf = new SessionOutputBufferImpl(1024, 128, params);
    HttpTransportMetricsImpl metrics = new HttpTransportMetricsImpl();
    LengthDelimitedEncoder encoder = new LengthDelimitedEncoder(channel, outbuf, metrics, 16);
    encoder.write(wrap("stuff;"));
    File tmpFile = File.createTempFile("testFile", "txt");
    FileOutputStream fout = new FileOutputStream(tmpFile);
    OutputStreamWriter wrtout = new OutputStreamWriter(fout);
    wrtout.flush();
    wrtout.close();
    FileChannel fchannel = new FileInputStream(tmpFile).getChannel();
    encoder.transfer(fchannel, 0, 20);
    encoder.write(wrap("more stuff"));
    String s = baos.toString("US-ASCII");
    assertTrue(encoder.isCompleted());
    assertEquals("stuff;more stuff", s);
    tmpFile.delete();
}

public boolean isValidPage(BookPage user) {
    boolean isValid = false;
    try {
        if (user.getType() == BookPage.TYPE_RESOURCE) {
            BookPagePreviewPanel panel = new BookPagePreviewPanel(dControl, true);
            panel.setCurrentBookPage(user);
            isValid = !user.getUri().equals("") && panel.isValid();
        } else if (user.getType() == BookPage.TYPE_URL) {
            URL file = new URL(user.getUri());
            file.openStream().close();
            isValid = true;
        } else if (user.getType() == BookPage.TYPE_IMAGE) {
            if (user.getUri().length() > 0) isValid = true;
        }
    } catch (Exception e) {
        isValid = false;
    }
    return isValid;
}

