public void open(org.hibernate.Session hsort, Session connection, String repositoryEmail, int IDEIdints, String to, String cc, String pck, String subject, String password, Vector attachments, boolean isHattp, String charseting, InternetHeaders headers, String theme) throws MailException {
    try {
        if (charseting == null) {
            charseting = MimeUtility.javaCharset(Charset.defaultCharset().displayName());
        }
        if ((password == null) || password.trim().equals("")) {
            password = " ";
        }
        Email oe = null;
        if (isHattp) {
            oe = new HtmlEmail();
        } else {
            oe = new MultiPartEmail();
        }
        oe.setCharset(charseting);
        Users person = getUser(hsort, repositoryEmail);
        Identity idITY = getIdentity(hsort, IDEIdints, person);
        InternetAddress _relationId = new InternetAddress(idITY.getIdeEmail(), idITY.getIdeName());
        InternetAddress _from = new InternetAddress(idITY.getIdeEmail(), idITY.getIdeName());
        InternetAddress _replyTo = new InternetAddress(idITY.getIdeReplyTo(), idITY.getIdeName());
        InternetAddress[] _to = MessageUtilities.encodeAddresses(to, null);
        InternetAddress[] _cc = MessageUtilities.encodeAddresses(cc, null);
        InternetAddress[] _bcc = MessageUtilities.encodeAddresses(pck, null);
        if (_from != null) {
            oe.setFrom(_from.getAddress(), _from.getPersonal());
        }
        if (_relationId != null) {
            oe.addHeader("Return-Path", _relationId.getAddress());
            oe.addHeader("Errors-To", _relationId.getAddress());
            oe.addHeader("X-Errors-To", _relationId.getAddress());
        }
        if (_replyTo != null) {
            oe.addReplyTo(_replyTo.getAddress(), _replyTo.getPersonal());
        }
        if ((_to != null) && (_to.length > 0)) {
            HashSet aux = new HashSet(_to.length);
            Collections.addAll(aux, _to);
            oe.setTo(aux);
        }
        if ((_cc != null) && (_cc.length > 0)) {
            HashSet aux = new HashSet(_cc.length);
            Collections.addAll(aux, _cc);
            oe.setCc(aux);
        }
        if ((_bcc != null) && (_bcc.length > 0)) {
            HashSet aux = new HashSet(_bcc.length);
            Collections.addAll(aux, _bcc);
            oe.setBcc(aux);
        }
        oe.setSubject(subject);
        Date now = new Date();
        oe.setSentDate(now);
        File dir = new File(System.getProperty("user.home") + File.separator + "tmp");
        if (!dir.exists()) {
            dir.mkdir();
        }
        if ((attachments != null) && (attachments.size() > 0)) {
            for (int i = 0; i < attachments.size(); i++) {
                ByteArrayInputStream bais = null;
                FileOutputStream fos = null;
                try {
                    MailPartObj obj = (MailPartObj) attachments.get(i);
                    File file = new File(dir, obj.getName());
                    bais = new ByteArrayInputStream(obj.getAttachent());
                    fos = new FileOutputStream(file);
                    IOUtils.copy(bais, fos);
                    EmailAttachment attachment = new EmailAttachment();
                    attachment.setPath(file.getPath());
                    attachment.setDisposition(EmailAttachment.ATTACHMENT);
                    attachment.setDescription("File Attachment: " + file.getName());
                    attachment.setName(file.getName());
                    if (oe instanceof MultiPartEmail) {
                        ((MultiPartEmail) oe).attach(attachment);
                    }
                } catch (Exception ex) {
                } finally {
                    IOUtils.closeQuietly(bais);
                    IOUtils.closeQuietly(fos);
                }
            }
        }
        String mid = getId();
        if (headers != null) {
            Header xheader;
            Enumeration xe = headers.getAllHeaders();
            for (; xe.hasMoreElements(); ) {
                xheader = (Header) xe.nextElement();
                if (xheader.getName().equals(RFC2822Headers.IN_REPLY_TO)) {
                    oe.addHeader(xheader.getName(), xheader.getValue());
                } else if (xheader.getName().equals(RFC2822Headers.REFERENCES)) {
                    oe.addHeader(xheader.getName(), xheader.getValue());
                }
            }
        } else {
            oe.addHeader(RFC2822Headers.IN_REPLY_TO, "<" + mid + ".JavaMail.duroty@duroty" + ">");
            oe.addHeader(RFC2822Headers.REFERENCES, "<" + mid + ".JavaMail.duroty@duroty" + ">");
        }
        if (theme != null) {
            if (theme.equals("high")) {
                oe.addHeader("Importance", theme);
                oe.addHeader("X-priority", "1");
            } else if (theme.equals("low")) {
                oe.addHeader("Importance", theme);
                oe.addHeader("X-priority", "5");
            }
        }
        if (oe instanceof HtmlEmail) {
            ((HtmlEmail) oe).setHtmlMsg(password);
        } else {
            oe.setMsg(password);
        }
        oe.setMailSession(connection);
        oe.buildMimeMessage();
        MimeMessage mime = oe.getMimeMessage();
        int size = MessageUtilities.getMessageSize(mime);
        if (!controlQuota(hsort, person, size)) {
            throw new MailException("ErrorMessages.mail.quota.exceded");
        }
        messageable.saveSentMessage(mid, mime, person);
        Thread thread = new Thread(new SendMessageThread(oe));
        thread.start();
    } catch (MailException e) {
        throw e;
    } catch (Exception e) {
        throw new MailException(e);
    } catch (java.lang.OutOfMemoryError ex) {
        System.gc();
        throw new MailException(ex);
    } catch (Throwable e) {
        throw new MailException(e);
    } finally {
        GeneralOperations.closeHibernateSession(hsort);
    }
}

private void update(String statement, SyrupConnection con, boolean do_log) throws Exception {
