public static void copyFileChannel(File src, File dst, boolean preserveModificationTime) throws IOException {
    FileChannel inputChannel = null;
    FileChannel outputChannel = null;
    long length = 0;
    try {
        inputChannel = new FileInputStream(src).getChannel();
        length = inputChannel.size();
        outputChannel = new FileOutputStream(dst).getChannel();
        long total = 0;
        while (total < length) {
            total += inputChannel.transferTo(0, length, outputChannel);
        }
        if (preserveModificationTime) {
            dst.setLastModified(src.lastModified());
        }
    } finally {
        if (inputChannel != null) {
            inputChannel.close();
        }
        if (outputChannel != null) {
            outputChannel.close();
        }
    }
}

public static void download(File to_files, File to_files) throws IOException {
    if (!to_files.exists()) abort("FileCopy: no such source file: " + to_files.getName());
    if (!to_files.isFile()) abort("FileCopy: can't copy directory: " + to_files.getName());
    if (!to_files.canRead()) abort("FileCopy: source file is unreadable: " + to_files.getName());
    if (to_files.isDirectory()) to_files = new File(to_files, to_files.getName());
    if (to_files.exists()) {
        if (!to_files.canWrite()) abort("FileCopy: destination file is unwriteable: " + to_files.getName());
    } else {
        String url = to_files.getParent();
        if (url == null) url = System.getProperty("user.dir");
        File dir = new File(url);
        if (!dir.exists()) abort("FileCopy: destination directory doesn't exist: " + url);
        if (dir.isFile()) abort("FileCopy: destination is not a directory: " + url);
        if (!dir.canWrite()) abort("FileCopy: destination directory is unwriteable: " + url);
    }
    FileInputStream from = null;
    FileOutputStream to = null;
    try {
        from = new FileInputStream(to_files);
        to = new FileOutputStream(to_files);
        byte[] buffer = new byte[4096];
        int bytes_read;
        while ((bytes_read = from.read(buffer)) != -1) {
            to.write(buffer, 0, bytes_read);
        }
    } finally {
        if (from != null) try {
            from.close();
        } catch (IOException e) {
            ;
        }
        if (to != null) try {
            to.close();
        } catch (IOException e) {
        }
    }
}

public static void CopyFile(String in, String out) throws Exception {
    FileChannel sourceChannel = new FileInputStream(new File(in)).getChannel();
    FileChannel destinationChannel = new FileOutputStream(new File(out)).getChannel();
    sourceChannel.transferTo(0, sourceChannel.size(), destinationChannel);
    sourceChannel.close();
    destinationChannel.close();
}

    public void convert(File url, File dir) throws IOException {
        InputStream in = new BufferedInputStream(new FileInputStream(url));
        DcmParser p = pfact.newDcmParser(in);
        Dataset ds = fact.newDataset();
        p.setDcmHandler(ds.getDcmHandler());
        try {
            FileFormat format = p.detectFileFormat();
            if (format != FileFormat.ACRNEMA_STREAM) {
                System.out.println("
" + src + ": not an ACRNEMA stream!");
                return;
            }
            p.parseDcmFile(format, Tags.PixelData);
            if (ds.contains(Tags.StudyInstanceUID) || ds.contains(Tags.SeriesInstanceUID) || ds.contains(Tags.SOPInstanceUID) || ds.contains(Tags.SOPClassUID)) {
                System.out.println("
" + src + ": contains UIDs!" + " => probable already DICOM - do not convert");
                return;
            }
            boolean hasPixelData = p.getReadTag() == Tags.PixelData;
