private ReaderFeed processEntris(String urlStr, String currentText) throws UnsupportedEncodingException, IOException, JDOMException {
    String key = "processEntrys@" + urlStr + "_" + currentText;
    if (cache.containsKey(key)) {
        return (ReaderFeed) cache.get(key);
    }
    List<Post> postList = new ArrayList<Post>();
    URL url = new URL(urlStr);
    HttpURLConnection connection = (HttpURLConnection) url.openConnection();
    connection.setRequestMethod("GET");
    connection.setRequestProperty("Cookie", "SID=" + sid);
    BufferedReader oder = new BufferedReader(new InputStreamReader(connection.getInputStream(), "UTF-8"));
    SAXBuilder build = new SAXBuilder(false);
    Document build = build.build(oder);
    Element html = build.getRootElement();
    Namespace grNamespaced = html.getNamespace("gr");
    Namespace namespace = html.getNamespace();
    String newflag = html.getChildText("continuation", grNamespaced);
    String title = html.getChildText("title", namespace);
    String subTitle = html.getChildText("subtitle", namespace);
    List<Element> entryList = html.getChildren("entry", namespace);
    DateFormat sdf = new SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss'Z'");
    for (Element e : entryList) {
        Post post = new Post();
        post.setTitle(e.getChildText("title", namespace));
        try {
            post.setDate(sdf.parse(e.getChildText("published", namespace)));
        } catch (ParseException e1) {
        }
        post.setUrl(e.getChild("link", namespace).getAttributeValue("href"));
        post.setSauthor(e.getChild("author", namespace).getChildText("name", namespace));
        String content = e.getChildText("content", namespace);
        if (StringUtils.isEmpty(content)) {
            content = e.getChildText("description", namespace);
        }
        if (StringUtils.isEmpty(content)) {
            content = e.getChildText("summary", namespace);
        }
        post.setContent(content);
        postList.add(post);
    }
    ReaderFeed readerFeed = new ReaderFeed();
    readerFeed.setTitle(title);
    readerFeed.setSubTitle(subTitle);
    readerFeed.setFlag(newflag);
    readerFeed.setPostList(postList);
    cache.put(key, readerFeed);
    return readerFeed;
}

