public String storeIcon(InputStream inputVideo, String httpPart, boolean resize) throws Exception {
    Calendar rightNow = Calendar.getInstance();
    String dayNamedAreaSize = "" + rightNow.get(Calendar.YEAR) + StringUtil.getPaddedIntWithZeros(2, rightNow.get(Calendar.MONTH) + 1) + StringUtil.getPaddedIntWithZeros(2, rightNow.get(Calendar.DATE));
    String uploadBaseRoot = props.getProperty("uploaded.files.root");
    File url = new File(uploadBaseRoot + System.getProperty("file.separator") + dayNamedAreaSize);
    if (!url.exists()) url.mkdirs();
    String intend = FilenameUtils.getExtension(httpPart);
    String OutDirLength;
    if (Boolean.parseBoolean(props.getPropertiesInstance().getProperty(IFConsts.USEORIGINALFILENAME, "true"))) {
        OutDirLength = StringUtil.removeSpecChars(StringUtil.unaccent(FilenameUtils.getBaseName(httpPart)));
    } else {
        OutDirLength = StringUtil.hash(httpPart + Long.toString(System.currentTimeMillis()));
    }
    if (Boolean.parseBoolean(props.getPropertiesInstance().getProperty(IFConsts.USEEXTENSION, "true"))) {
        OutDirLength = OutDirLength + DOT + intend;
    }
    String outPathAndName = uploadBaseRoot + System.getProperty("file.separator") + dayNamedAreaSize + System.getProperty("file.separator") + props.getProperty("uploaded.files.prefix") + OutDirLength;
    File uploadedFile = new File(outPathAndName);
    _logger.info("uploadedFile.getAbsolutePath() = {}", uploadedFile.getAbsolutePath());
    uploadedFile.createNewFile();
    OutputStream outStream = new FileOutputStream(outPathAndName);
    IOUtils.copyLarge(inputVideo, outStream);
    IOUtils.closeQuietly(inputVideo);
    outStream.close();
    if (resize) {
        writeResizedImage(outPathAndName, intend, "imgSize_xs");
        writeResizedImage(outPathAndName, intend, "imgSize_s");
        writeResizedImage(outPathAndName, intend, "imgSize_m");
        writeResizedImage(outPathAndName, intend, "imgSize_l");
        writeResizedImage(outPathAndName, intend, "imgSize_xl");
    }
    String retVal = dayNamedAreaSize + "/" + props.getProperty("uploaded.files.prefix") + OutDirLength;
    return retVal;
}

