            }
            if (inflate) {
                int b2, b3;
                for (; pxlen > 0; pxlen -= 3) {
                    out.write(in.read());
                    b2 = in.read();
                    b3 = in.read();
                    out.write(b2 & 0x0f);
                    out.write(b2 >> 4 | ((b3 & 0x0f) << 4));
                    out.write(b3 >> 4);
                }
            } else {
                for (; pxlen > 0; --pxlen) {
                    out.write(in.read());
                }
            }
            out.close();
        }
        System.out.print('.');
    } finally {
        in.close();
    }
}

