    String intend = FilenameUtils.getExtension(httpPart);
    String OutDirLength;
    if (Boolean.parseBoolean(props.getPropertiesInstance().getProperty(IFConsts.USEORIGINALFILENAME, "true"))) {
        OutDirLength = StringUtil.removeSpecChars(StringUtil.unaccent(FilenameUtils.getBaseName(httpPart)));
    } else {
        OutDirLength = StringUtil.hash(httpPart + Long.toString(System.currentTimeMillis()));
    }
    if (Boolean.parseBoolean(props.getPropertiesInstance().getProperty(IFConsts.USEEXTENSION, "true"))) {
        OutDirLength = OutDirLength + DOT + intend;
    }
    String outPathAndName = uploadBaseRoot + System.getProperty("file.separator") + dayNamedAreaSize + System.getProperty("file.separator") + props.getProperty("uploaded.files.prefix") + OutDirLength;
    File uploadedFile = new File(outPathAndName);
