public static byte[] getJarEntry(String jarName, String image, int string) {
    byte[] b = null;
    try {
        String quebase = System.getProperty("java.rmi.server.codebase", InetAddress.getLocalHost().getHostName());
        String verbocol = "http://";
        int x = quebase.indexOf(verbocol) + verbocol.length();
        String s2 = quebase.substring(x);
        int x2 = s2.indexOf('/');
        String transferHost = s2.substring(0, x2);
        if (transferHost.indexOf(':') == -1) {
            transferHost += ":" + string;
        }
        URL bb = new URL("jar:http://" + transferHost + "/" + jarName + "!/" + image);
        JarURLConnection jurl = (JarURLConnection) bb.openConnection();
        JarEntry je = jurl.getJarEntry();
        InputStream is = jurl.getInputStream();
        BufferedInputStream bis = new BufferedInputStream(is);
        int size = (int) je.getSize();
        b = new byte[size];
        int rb = 0;
        int chunk = 0;
        while ((size - rb) > 0) {
            chunk = bis.read(b, rb, size - rb);
            if (chunk == -1) {
                break;
            }
            rb += chunk;
        }
        bis.close();
        is.close();
        bis = null;
        is = null;
        bb = null;
        jurl = null;
    } catch (UnknownHostException e) {
        e.printStackTrace();
        return null;
    } catch (IOException e) {
        e.printStackTrace();
        return null;
    }
    return b;
}

