public void execute() {
    try {
        if (methods == null) {
            return;
        }
        List<Resource> resources = new ArrayList<Resource>();
        for (Iterator<Resource> i = classFiles.iterator(); i.hasNext(); ) {
            final Resource finalUrl = i.next();
            if (!finalUrl.getName().equals(CALLSTACK_FILE)) {
                try {
                    InputStream rawSteam = finalUrl.getInputStream();
                    ClassReader reader = new ClassReader(rawSteam);
                    reader.accept(new MethodAnalyzer(), 0);
                    resources.add(finalUrl);
                    oldSize += finalUrl.getSize();
                } catch (IllegalStateException e) {
                }
            }
        }
        for (Iterator<Resource> i = resources.iterator(); i.hasNext(); ) {
            final Resource finalUrl = i.next();
            try {
                InputStream rawSteam = finalUrl.getInputStream();
                ClassReader reader = new ClassReader(rawSteam);
                ClassWriter writer = new ClassWriter(reader, ClassWriter.COMPUTE_MAXS);
                if (!finalUrl.getName().startsWith(CALLSTACK_FILE.substring(0, CALLSTACK_FILE.indexOf('.')))) {
                    reader.accept(new MethodInjector(writer), 0);
                    byte[] b = writer.toByteArray();
                    rawSteam.close();
                    OutputStream outputStream = finalUrl.getOutputStream();
                    outputStream.write(b);
                    outputStream.close();
                    injectedClasses++;
                }
            } catch (IllegalStateException e) {
            }
            newSize += finalUrl.getSize();
        }
        File finalUrl = new File(classFiles.getDir(), CALLSTACK_FILE);
        if (!finalUrl.exists()) {
            throw new BuildException("Missing net.yura.mobile.logging.CallStack class");
        }
        InputStream rawSteam = new FileInputStream(finalUrl);
        ClassReader reader = new ClassReader(rawSteam);
        ClassWriter writer = new ClassWriter(reader, ClassWriter.COMPUTE_MAXS);
        try {
            reader.accept(new PushMethodGenerator(writer), 0);
        } finally {
            rawSteam.close();
        }
        byte[] b = writer.toByteArray();
        rawSteam.close();
        OutputStream outputStream = new FileOutputStream(finalUrl);
        outputStream.write(b);
        outputStream.close();
        log(injectedClasses + " classes injected");
        log((injectedMethods * 100) / Math.max(1, totalMethods) + "% of methods injected");
        log((100 - ((oldSize * 100) / newSize)) + "% code size increase");
    } catch (IOException e) {
        log(e, 0);
    }
}

