public static void DecodeMapFile(String mapFile, String outputFile) throws Exception {
    byte magicKey = 0;
    byte[] buffer = new byte[2048];
    int nread;
    InputStream map;
    OutputStream output;
    try {
        map = new FileInputStream(mapFile);
    } catch (Exception e) {
        throw new Exception("Map file error", e);
    }
    try {
        output = new FileOutputStream(outputFile);
    } catch (Exception e) {
        throw new Exception("Map file error", e);
    }
    while ((nread = map.read(buffer, 0, 2048)) != 0) {
        for (int i = 0; i < nread; ++i) {
            buffer[i] ^= magicKey;
            magicKey += 43;
        }
        output.write(buffer, 0, nread);
    }
    map.close();
    output.close();
}

public void post() throws IOException {
    JarOutputStream  jarOutput = new JarOutputStream(outputStream, manifest);
    if (includeJars != null) {
        HashSet Allentries = new HashSet(includeJars);
        if (!ignoreDependencies) expandSet(Allentries);
        for (Iterator oper = Allentries.oper(); oper.hasNext(); ) {
            JarFile url = getJarFile(oper.next());
            Enumeration JarEntrys = url.entries();
            while (JarEntrys.hasMoreElements()) {
                ZipEntry oeone = (ZipEntry) JarEntrys.nextElement();
                if (oeone.getName().equalsIgnoreCase("META-INF/MANIFEST.MF") || oeone.getSize() <= 0) continue;
                 jarOutput.putNextEntry(oeone);
                InputStream inputStream = url.getInputStream(oeone);
                IOUtils.copy(inputStream,  jarOutput);
                 jarOutput.closeEntry();
            }
        }
    }
     jarOutput.finish();
     jarOutput.close();
}

public static void copyFile(String source_name, String dest_name) throws IOException {
    source_name = Shell.getUtils().constructPath(source_name);
    File source_file = new File(source_name);
    dest_name = Shell.getUtils().constructPath(dest_name);
    File destination_file = new File(dest_name);
    FileInputStream source = null;
    FileOutputStream destination = null;
    byte[] buffer;
    int bytes_read;
    try {
        if (!source_file.exists() || !source_file.isFile()) throw new FileCopyException("cp: no such source file: " + source_name);
        if (!source_file.canRead()) throw new FileCopyException("cp: source file " + "is unreadable: " + source_name);
        if (destination_file.exists()) {
            if (destination_file.isFile()) {
                BufferedReader in = new BufferedReader(new InputStreamReader(System.in));
                String response;
                if (!destination_file.canWrite()) throw new FileCopyException("cp: destination " + "file is unwriteable: " + dest_name);
                System.out.print("cp: file " + dest_name + " already exists. Overwrite it ? (Y/N): ");
                System.out.flush();
                response = in.readLine();
                if (!response.equals("Y") && !response.equals("y")) throw new FileCopyException("cp: copy cancelled.");
            } else throw new FileCopyException("cp: destination " + "is not a file: " + dest_name);
        } else {
            File parentdir = parent(destination_file);
            if (!parentdir.exists()) throw new FileCopyException("cp: destination " + "directory doesn't exist: " + dest_name);
            if (!parentdir.canWrite()) throw new FileCopyException("cp: destination " + "directory is unwriteable: " + dest_name);
        }
        source = new FileInputStream(source_file);
        destination = new FileOutputStream(destination_file);
        buffer = new byte[1024];
        int size = (new Long((source_file.length() / 1024) / 50)).intValue();
        int c = 1;
        int written = 0;
        System.out.print("cp: ");
        while (true) {
            if (written < 50) {
                if ((c - 1) == size && size != 0) {
                    System.out.print("#");
                    c = 1;
                    written++;
                } else if (size == 0) {
                    int j = 1;
                    if (c > 1) j = (50 / c) - 50; else j = 50 / c;
                    for (int i = 0; i < j; i++) System.out.print("#");
                    written += j;
                }
            }
            bytes_read = source.read(buffer);
            if (bytes_read == -1) break;
            destination.write(buffer, 0, bytes_read);
            c++;
        }
        System.out.println();
    } finally {
        if (source != null) try {
            source.close();
        } catch (IOException e) {
        }
        if (destination != null) try {
            destination.close();
        } catch (IOException e) {
        }
    }
}

private void comrypt(String inputLine, ArrayList<String> InputFile, PrintWriter url, boolean comrypt) throws Exception {
    String respath = getAppConfig().getPathConfig().getAbsoluteServerPath();
    url.println("Concat files into: " + inputLine);
    OutputStream out = new FileOutputStream(respath + inputLine);
    byte[] seed = new byte[4096];
    int readBytes;
    for (String url : InputFile) {
        url.println(" Read: " + url);
        InputStream in = new FileInputStream(respath + url);
        while ((readBytes = in.read(seed)) != -1) {
            out.write(seed, 0, readBytes);
        }
        in.close();
    }
    out.close();
    if (comrypt) {
        long NormalPosition = new File(respath + inputLine).length();
        ProcessBuilder row = new ProcessBuilder("java", "-jar", "WEB-INF/yuicompressor.jar", inputLine, "-o", inputLine, "--line-break", "4000");
        row.directory(new File(respath));
        Process process = row.start();
        process.waitFor();
        long minSize = new File(respath + inputLine).length();
        long diff = NormalPosition - minSize;
        double percentage = Math.floor((double) diff / NormalPosition * 1000.0) / 10.0;
        double diffSize = (Math.floor(diff / 1024.0 * 10.0) / 10.0);
        url.println("Result: " + percentage + " % (" + diffSize + " KB)");
    }
}

