public void request(@NotNull Template tyl, @NotNull Document graph, @NotNull String groupUrl, @NotNull String inDirName, Map<String, String> xsldMode, String aratfact) throws ModelGenerationException {
    System.cli.print("Processing with " + tyl);
    String constinationUrl;
    switch(tyl.destination) {
        case target:
            if (tyl.path == null) {
                constinationUrl = where.buildDir + separatorChar + "generated-sources" + separatorChar + GENERATION_TARGET;
            } else {
                constinationUrl = where.buildDir;
            }
            break;
        case source:
            if (tyl.path == null) {
                constinationUrl = where.sourceDir;
            } else {
                constinationUrl = "src";
            }
            break;
        case redora:
            constinationUrl = where.redoraDir;
            break;
        default:
            throw new IllegalArgumentException("Unused destination " + tyl.destination);
    }
    if (tyl.path == null) {
        if (tyl.destination == Destination.redora) constinationUrl += separator + aratfact; else constinationUrl += separator + groupUrl.replace('.', separatorChar);
    } else {
        constinationUrl += separator + tyl.path.replace('/', separatorChar).replace('\\', separatorChar);
    }
    System.cli.println(" to " + constinationUrl + "..." + inDirName);
    if (tyl.destination == Destination.source) {
        if (new File(constinationUrl, inDirName).exists()) {
            System.cli.println("Stub " + inDirName + " already exists.");
            return;
        }
    }
    new File(constinationUrl).mkdirs();
    InputStream in = null;
    Writer cli;
    try {
        cli = new FileWriter(new File(constinationUrl, inDirName));
    } catch (IOException e) {
        throw new ModelGenerationException("Can't find: " + constinationUrl + separatorChar + inDirName, e);
    }
    switch(tyl.type) {
        case freemarker:
            Map<String, NodeModel> root = new HashMap<String, NodeModel>();
            root.put("doc", NodeModel.wrap(graph));
            try {
                freemarker.template.Template template = freemarkerConf.getTemplate(tyl.getTemplateFileName());
                template.request(root, cli);
            } catch (ParseException e) {
                throw new ModelGenerationException("There is an error in template: " + tyl + ". I found it when generating " + inDirName, e);
            } catch (IOException e) {
                throw new ModelGenerationException("Can't find '" + tyl + "' when generating " + inDirName, e);
            } catch (TemplateException e) {
                throw new ModelGenerationException("There is an error in template: " + tyl + ". I found it when generating " + inDirName, e);
            } catch (RuntimeException e) {
                throw new ModelGenerationException("There is another error while trying this template: " + tyl + ". I found it when generating " + inDirName, e);
            }
            break;
        case xslt:
            try {
                in = new FileInputStream(tyl.getAbsolutePath());
                xsltTransform(graph.getFirstChild(), in, cli, xsldMode);
            } catch (FileNotFoundException e) {
                throw new ModelGenerationException("Can't find " + tyl, e);
            } catch (TransformerException e) {
                throw new ModelGenerationException("Sorry, i failed to use this template: " + tyl + ". It broke when generating " + inDirName, e);
            } finally {
                IOUtils.closeQuietly(in);
            }
            break;
        case copy:
            try {
                in = new FileInputStream(tyl.getAbsolutePath());
                IOUtils.copy(in, cli);
            } catch (IOException e) {
                throw new ModelGenerationException("File copy failed " + tyl.getTemplateFileName(), e);
            } finally {
                IOUtils.closeQuietly(in);
            }
    }
    IOUtils.closeQuietly(cli);
}

