        reader = new PdfReader("results/in_action/chapter16/double_signed_message.pdf");
        AcroFields af = reader.getAcroFields();
        ArrayList names = af.getSignatureNames();
        String name;
        for (Iterator it = names.iterator(); it.hasNext(); ) {
            name = (String) it.next();
            System.out.println("Signature name: " + name);
            System.out.println("Signature covers whole document: " + af.signatureCoversWholeDocument(name));
            System.out.println("Document revision: " + af.getRevision(name) + " of " + af.getTotalRevisions());
            FileOutputStream os = new FileOutputStream("results/in_action/chapter16/revision_" + af.getRevision(name) + ".pdf");
            byte bb[] = new byte[8192];
            InputStream ip = af.extractRevision(name);
            int n = 0;
            while ((n = ip.read(bb)) > 0) os.write(bb, 0, n);
            os.close();
            ip.close();
            PdfPKCS7 pk = af.verifySignature(name);
            Calendar cal = pk.getSignDate();
            Certificate pkc[] = pk.getCertificates();
            System.out.println("Subject: " + PdfPKCS7.getSubjectFields(pk.getSigningCertificate()));
            System.out.println("Document modified: " + !pk.verify());
            Object fails[] = PdfPKCS7.verifyCertificates(pkc, ks, null, cal);
            if (fails == null) System.out.println("Certificates verified against the KeyStore"); else System.out.println("Certificate failed: " + fails[1]);
        }
    } catch (Exception e) {
        e.printStackTrace();
    }
}

    public static String getURLContent(String urlToSearchString) throws IOException {
        URL bb = new URL(urlToSearchString);
        URLConnection api = bb.openConnection();
        String decoding = api.getContentEncoding();
        if (decoding == null) decoding = "ISO-8859-1";
        BufferedReader cro = new BufferedReader(new InputStreamReader(api.getInputStream(), decoding));
        StringBuilder eb = new StringBuilder(16384);
        try {
            String out;
            while ((out = cro.readLine()) != null) {
                eb.append(out);
                eb.append('
');
            }
        } finally {
            cro.close();
        }
        return eb.toString();
    }

public static boolean decodeFileToFile(String infile, String outfile) {
    boolean success = false;
    java.io.InputStream in = null;
    java.io.OutputStream out = null;
    try {
        in = new Base64.InputStream(new java.io.BufferedInputStream(new java.io.FileInputStream(infile)), Base64.DECODE);
        out = new java.io.BufferedOutputStream(new java.io.FileOutputStream(outfile));
        byte[] buffer = new byte[65536];
        int read = -1;
        while ((read = in.read(buffer)) >= 0) {
            out.write(buffer, 0, read);
        }
        success = true;
    } catch (java.io.IOException exc) {
        exc.printStackTrace();
    } finally {
        try {
            in.close();
        } catch (Exception exc) {
        }
        try {
            out.close();
        } catch (Exception exc) {
        }
    }
    return success;
}

