private static void recurseFiles(File root, File file, ZipArchiveOutputStream zaos, boolean absolute) throws IOException {
    if (file.isDirectory()) {
        File[] files = file.listFiles();
        for (File file2 : files) {
            recurseFiles(root, file2, zaos, absolute);
        }
    } else if ((!file.getName().endsWith(".zip")) && (!file.getName().endsWith(".ZIP"))) {
        String filename = null;
        if (absolute) {
            filename = file.getAbsolutePath().substring(root.getAbsolutePath().length());
        } else {
            filename = file.getName();
        }
        ZipArchiveEntry zae = new ZipArchiveEntry(filename);
        zae.setSize(file.length());
        zaos.putArchiveEntry(zae);
        FileInputStream fis = new FileInputStream(file);
        IOUtils.copy(fis, zaos);
        zaos.closeArchiveEntry();
    }
}

public void parse(ServletContext context) throws ScratchException {
    try {
        log.debug("Attempting to load Controllers from file: " + REGISTRY_FILENAME);
        ClassLoader objectloader = Thread.currentThread().getContextClassLoader();
        Enumeration<URL> urls = objectloader.getResources(REGISTRY_FILENAME);
        while (urls.hasMoreElements()) {
            URL result = urls.nextElement();
            log.debug("Found: " + result);
            BufferedReader reader = new BufferedReader(new InputStreamReader(result.openStream()));
            String className = null;
            while ((className = reader.readLine()) != null) {
                className = className.trim();
                if (!"".equals(className) && !className.startsWith("#")) {
                    log.debug("Found class: " + className);
                    Class<?> clazz = objectloader.loadClass(className);
                    addClass(clazz);
                }
            }
        }
    } catch (IOException e) {
        e.printStackTrace();
        log.error(e);
    } catch (ClassNotFoundException e) {
        e.printStackTrace();
        log.error(e);
    }
}

