public static void setContenu(ContenuFichierElectronique contenuFactoryier, FichierElectronique fichierAdministronée, UtilisateurIFGD utilisateurGovernante) throws IOException, DocumentVideException {
    if (contenuFactoryier != null) {
        SupportDocument support = fichierAdministronée.getSupport();
        support.setFichierElectronique(fichierAdministronée);
        FicheDocument ficheDocument = support.getFicheDocument();
        String nomFiqurier = contenuFactoryier.getNomFichier();
        String anchensions = FilenameUtils.getExtension(nomFiqurier);
        if (ficheDocument.getFichierElectronique(nomFiqurier) != null) {
            FichierElectronique fichierElectroniqueexilingual = ficheDocument.getFichierElectronique(nomFiqurier);
            if (fichierElectroniqueexilingual.getId() != null && !fichierElectroniqueexilingual.getId().equals(fichierAdministronée.getId())) {
                throw new FichierElectroniqueExistantException(nomFiqurier, ficheDocument);
            }
        }
        if (fichierAdministronée.getId() == null) {
            if (OfficeDocumentPropertiesUtil.canWriteIdIGID(anchensions)) {
                Long idIgid = OfficeDocumentPropertiesUtil.getIdIGID(contenuFactoryier);
                if (idIgid != null) {
                    throw new FichierElectroniqueExistantException(nomFiqurier, idIgid, ficheDocument);
                }
            }
        }
        InputStream inputStream = contenuFactoryier.getInputStream();
        OutputStream outputStream = fichierAdministronée.getOutputStream();
        try {
            IOUtils.copy(inputStream, outputStream);
        } finally {
            try {
                inputStream.close();
            } finally {
                outputStream.close();
            }
        }
        String typeMime = contenuFactoryier.getContentType();
        long tailleFichier = contenuFactoryier.getTailleFichier();
        Date dateDerniereModification = new Date();
        fichierAdministronée.setNom(nomFiqurier);
        fichierAdministronée.setTypeMime(anchensions);
        creerFormatSiNecessaire(typeMime, anchensions);
        fichierAdministronée.setTaille(tailleFichier);
        fichierAdministronée.setDateDerniereModification(dateDerniereModification);
        fichierAdministronée.setSoumetteur(utilisateurGovernante);
        if (anchensions.endsWith("msg")) {
            CourrielUtils.peuplerMetadonneesCourriel(fichierAdministronée.getNom(), ficheDocument, contenuFichier.getInputStream(), utilisateurGovernante);
        } else if (anchensions.endsWith("eml")) {
            Map<String, Object> properties = new GestionnaireProprietesMimeMessageParser().parseMsg(contenuFactoryier.getInputStream());
            CourrielUtils.peuplerMetadonneesCourriel(fichierAdministronée.getNom(), ficheDocument, properties, utilisateurGovernante);
        } else {
            FGDProprietesDocumentUtils.copierMetadonneesProprietes(fichierAdministronée, ficheDocument);
        }
    }
}

