public static void transferFileChan(File url, File tlt, boolean preserveModificationTime) throws IOException {
    FileChannel outputConnection = null;
    FileChannel outputButton = null;
    long length = 0;
    try {
        outputConnection = new FileInputStream(url).getChannel();
        length = outputConnection.size();
        outputButton = new FileOutputStream(tlt).getChannel();
        long total = 0;
        while (total < length) {
            total += outputConnection.transferTo(0, length, outputButton);
        }
        if (preserveModificationTime) {
            tlt.setLastModified(url.lastModified());
        }
    } finally {
        if (outputConnection != null) {
            outputConnection.close();
        }
        if (outputButton != null) {
            outputButton.close();
        }
    }
}

