protected InputStream callApiPost(String apiUrl, Map<String, List<String>> parameters, int expected) {
    try {
        URL url = new URL(apiUrl);
        HttpURLConnection request = (HttpURLConnection) url.openConnection();
        if (ApplicationConstants.CONNECT_TIMEOUT > -1) {
            request.setConnectTimeout(ApplicationConstants.CONNECT_TIMEOUT);
        }
        if (ApplicationConstants.READ_TIMEOUT > -1) {
            request.setReadTimeout(ApplicationConstants.READ_TIMEOUT);
        }
        for (String headerName : requestHeaders.keySet()) {
            request.setRequestProperty(headerName, requestHeaders.get(headerName));
        }
        request.setRequestMethod("POST");
        request.setDoOutput(true);
        PrintStream out = new PrintStream(new BufferedOutputStream(request.getOutputStream()));
        out.print(getParametersString(parameters));
        out.flush();
        out.close();
        request.connect();
        if (request.getResponseCode() != expected) {
            throw new BingMapsException(convertStreamToString(request.getErrorStream()));
        } else {
            return getWrappedInputStream(request.getInputStream(), GZIP_ENCODING.equalsIgnoreCase(request.getContentEncoding()));
        }
    } catch (IOException e) {
        throw new BingMapsException(e);
    }
}

public static InputStream getStreamFromSystemIdifier(String systemPath, EntityResolver Resolve) throws Exception {
    InputSource source = null;
    InputStream stream = null;
    if (Resolve != null) {
        try {
            source = Resolve.resolveEntity(null, systemPath);
        } catch (Exception e) {
            LogService.instance().log(LogService.ERROR, "DocumentFactory: Unable to resolve '" + systemPath + "'");
            LogService.instance().log(LogService.ERROR, e);
        }
    }
    if (source != null) {
        try {
            stream = source.getByteStream();
        } catch (Exception e) {
            LogService.instance().log(LogService.ERROR, "DocumentFactory: Unable to get bytestream from '" + source.getSystemId() + "'");
            LogService.instance().log(LogService.ERROR, e);
        }
    }
    if (stream == null) {
        URL path = new URL(systemPath);
        stream = path.openStream();
    }
    return stream;
}

public static void copyTo(File source, File dest) {
    if (source.isHidden()) ; else if (source.isDirectory()) {
        File temp = new File(dest.getPath() + "/" + source.getName());
        temp.mkdir();
        for (File sel : source.listFiles()) copyTo(sel, temp);
    } else {
        try {
            File tempDest = new File(dest.getPath() + "/" + source.getName());
            tempDest.createNewFile();
            FileChannel sourceCh = new FileInputStream(source).getChannel();
            FileChannel destCh = new FileOutputStream(tempDest).getChannel();
            sourceCh.transferTo(0, sourceCh.size(), destCh);
            sourceCh.close();
            destCh.close();
        } catch (IOException ex) {
            Logger.getLogger(EditorUtil.class.getName()).log(Level.SEVERE, null, ex);
        }
    }
}

public String storeIcon(InputStream inputVideo, String httpPart, boolean resize) throws Exception {
    Calendar rightNow = Calendar.getInstance();
    String dayNamedAreaSize = "" + rightNow.get(Calendar.YEAR) + StringUtil.getPaddedIntWithZeros(2, rightNow.get(Calendar.MONTH) + 1) + StringUtil.getPaddedIntWithZeros(2, rightNow.get(Calendar.DATE));
    String uploadBaseRoot = props.getProperty("uploaded.files.root");
    File url = new File(uploadBaseRoot + System.getProperty("file.separator") + dayNamedAreaSize);
    if (!url.exists()) url.mkdirs();
    String intend = FilenameUtils.getExtension(httpPart);
    String OutDirLength;
    if (Boolean.parseBoolean(props.getPropertiesInstance().getProperty(IFConsts.USEORIGINALFILENAME, "true"))) {
        OutDirLength = StringUtil.removeSpecChars(StringUtil.unaccent(FilenameUtils.getBaseName(httpPart)));
    } else {
        OutDirLength = StringUtil.hash(httpPart + Long.toString(System.currentTimeMillis()));
    }
    if (Boolean.parseBoolean(props.getPropertiesInstance().getProperty(IFConsts.USEEXTENSION, "true"))) {
        OutDirLength = OutDirLength + DOT + intend;
    }
    String outPathAndName = uploadBaseRoot + System.getProperty("file.separator") + dayNamedAreaSize + System.getProperty("file.separator") + props.getProperty("uploaded.files.prefix") + OutDirLength;
    File uploadedFile = new File(outPathAndName);
    _logger.info("uploadedFile.getAbsolutePath() = {}", uploadedFile.getAbsolutePath());
    uploadedFile.createNewFile();
    OutputStream outStream = new FileOutputStream(outPathAndName);
    IOUtils.copyLarge(inputVideo, outStream);
    IOUtils.closeQuietly(inputVideo);
    outStream.close();
    if (resize) {
        writeResizedImage(outPathAndName, intend, "imgSize_xs");
        writeResizedImage(outPathAndName, intend, "imgSize_s");
        writeResizedImage(outPathAndName, intend, "imgSize_m");
        writeResizedImage(outPathAndName, intend, "imgSize_l");
        writeResizedImage(outPathAndName, intend, "imgSize_xl");
    }
    String retVal = dayNamedAreaSize + "/" + props.getProperty("uploaded.files.prefix") + OutDirLength;
    return retVal;
}

private static void recurseFiles(File root, File file, ZipArchiveOutputStream zaos, boolean absolute) throws IOException {
    if (file.isDirectory()) {
        File[] files = file.listFiles();
        for (File file2 : files) {
            recurseFiles(root, file2, zaos, absolute);
        }
    } else if ((!file.getName().endsWith(".zip")) && (!file.getName().endsWith(".ZIP"))) {
        String filename = null;
        if (absolute) {
            filename = file.getAbsolutePath().substring(root.getAbsolutePath().length());
        } else {
            filename = file.getName();
        }
        ZipArchiveEntry zae = new ZipArchiveEntry(filename);
        zae.setSize(file.length());
        zaos.putArchiveEntry(zae);
        FileInputStream fis = new FileInputStream(file);
        IOUtils.copy(fis, zaos);
        zaos.closeArchiveEntry();
    }
}

    public static Collection<Tuple<String, ArrayList<String>>> readGeoParserResult(String recordFile, boolean getGazeteerIds) {
        if (TESTING) {
            HashSet<Tuple<String, ArrayList<String>>> ret = new HashSet<Tuple<String, ArrayList<String>>>();
            ret.add(new Tuple<String, ArrayList<String>>("teste", new ArrayList<String>()));
            return ret;
        }
        int retries = 0;
        while (retries < 3) {
            try {
                ArrayList<Tuple<String, ArrayList<String>>> ret = new ArrayList<Tuple<String, ArrayList<String>>>();
                String reqPre = "<?xml version=\"1.0\"?>\r
" + (getGazeteerIds ? "<GetFeature" : "<GetParsing") + " xmlns=\"http://www.opengis.net/gp\" xmlns:wfs=\"http://www.opengis.net/wfs\"" + " xmlns:xsi=\"http://www.w3.org/2000/10/XMLSchema-instance\"" + " xsi:schemaLocation=\"http://www.opengis.net/gp ../gp/GetFeatureRequest.xsd http://www.opengis.net/wfs ../wfs/GetFeatureRequest.xsd\"\r
" + " wfs:outputFormat=\"GML2\">" + "<wfs:Query wfs:TypeName=\"PlaceName\" />" + "<Resource mine=\"text/plain\">" + "<Contents></Contents>" + "</Resource>" + (getGazeteerIds ? "</GetFeature>" : "</GetParsing>");
                Document doc = DocumentHelper.parseText(reqPre);
                doc.getRootElement().element("Resource").element("Contents").setText(recordFile);
                URL url = new URL(geoParserBaseUrl + "?request=" + URLEncoder.encode(doc.asXML(), "ISO8859-1"));
                InputStreamReader reader = new InputStreamReader(url.openStream(), "UTF-8");
                BufferedReader buffered = new BufferedReader(reader);
                StringBuffer sb = new StringBuffer();
                String line;
                while ((line = buffered.readLine()) != null) {
                    sb.append(line);
                }
                Document d = DocumentHelper.parseText(sb.toString());
                HashSet<String> places = new HashSet<String>();
                for (Iterator<Element> it = d.getRootElement().element("EntryCollection").elementIterator("PlaceName"); it.hasNext(); ) {
                    Element plcEl = it.next();
                    String val = plcEl.elementTextTrim("TermName");
                    if (!val.equals("") && !places.contains(val)) {
                        places.add(val);
                        String entryID = plcEl.attributeValue("entryID");
                        Tuple<String, ArrayList<String>> plc = new Tuple<String, ArrayList<String>>(val, new ArrayList<String>());
                        for (Iterator<Element> it2 = d.getRootElement().element("EntryCollection").elementIterator("GazetteerEntry"); it2.hasNext(); ) {
                            Element idEl = it2.next();
                            if (idEl.attributeValue("entryID").equals(entryID)) plc.getV2().add(idEl.attributeValue("id"));
                        }
                        ret.add(plc);
                    }
                }
                return ret;
            } catch (Exception e) {
                log.debug("Erro ao pesquisar a lista de termos para o registo " + recordFile + "! " + e.getMessage(), e);
                System.out.println("Erro ao pesquisar a lista de termos para o registo " + recordFile + "! " + e.getMessage());
                retries++;
            }
        }
        System.out.println("Too many retries. Giving up.");
        return new HashSet<Tuple<String, ArrayList<String>>>();
    }

