    public void convert(File url, File dir) throws IOException {
        InputStream in = new BufferedInputStream(new FileInputStream(url));
        DcmParser p = pfact.newDcmParser(in);
        Dataset ds = fact.newDataset();
        p.setDcmHandler(ds.getDcmHandler());
        try {
            FileFormat format = p.detectFileFormat();
            if (format != FileFormat.ACRNEMA_STREAM) {
                System.out.println("
" + src + ": not an ACRNEMA stream!");
                return;
            }
            p.parseDcmFile(format, Tags.PixelData);
            if (ds.contains(Tags.StudyInstanceUID) || ds.contains(Tags.SeriesInstanceUID) || ds.contains(Tags.SOPInstanceUID) || ds.contains(Tags.SOPClassUID)) {
                System.out.println("
" + src + ": contains UIDs!" + " => probable already DICOM - do not convert");
                return;
            }
            boolean hasPixelData = p.getReadTag() == Tags.PixelData;
            boolean inflate = hasPixelData && ds.getInt(Tags.BitsAllocated, 0) == 12;
            int pxlen = p.getReadLength();
            if (hasPixelData) {
                if (inflate) {
                    ds.putUS(Tags.BitsAllocated, 16);
                    pxlen = pxlen * 4 / 3;
                }
                if (pxlen != (ds.getInt(Tags.BitsAllocated, 0) >>> 3) * ds.getInt(Tags.Rows, 0) * ds.getInt(Tags.Columns, 0) * ds.getInt(Tags.NumberOfFrames, 1) * ds.getInt(Tags.NumberOfSamples, 1)) {
                    System.out.println("
" + src + ": mismatch pixel data length!" + " => do not convert");
                    return;
                }
            }
            ds.putUI(Tags.StudyInstanceUID, uid(studyUID));
            ds.putUI(Tags.SeriesInstanceUID, uid(seriesUID));
            ds.putUI(Tags.SOPInstanceUID, uid(instUID));
            ds.putUI(Tags.SOPClassUID, classUID);
            if (!ds.contains(Tags.NumberOfSamples)) {
                ds.putUS(Tags.NumberOfSamples, 1);
            }
            if (!ds.contains(Tags.PhotometricInterpretation)) {
                ds.putCS(Tags.PhotometricInterpretation, "MONOCHROME2");
            }
            if (fmi) {
                ds.setFileMetaInfo(fact.newFileMetaInfo(ds, UIDs.ImplicitVRLittleEndian));
            }
            OutputStream out = new BufferedOutputStream(new FileOutputStream(dir));
            try {
            } finally {
                ds.writeFile(out, encodeParam());
                if (hasPixelData) {
                    if (!skipGroupLen) {
                        out.write(PXDATA_GROUPLEN);
                        int grlen = pxlen + 8;
                        out.write((byte) grlen);
                        out.write((byte) (grlen >> 8));
                        out.write((byte) (grlen >> 16));
                        out.write((byte) (grlen >> 24));
                    }
                    out.write(PXDATA_TAG);
                    out.write((byte) pxlen);
                    out.write((byte) (pxlen >> 8));
                    out.write((byte) (pxlen >> 16));
                    out.write((byte) (pxlen >> 24));
                }
                if (inflate) {
                    int b2, b3;
                    for (; pxlen > 0; pxlen -= 3) {
                        out.write(in.read());
                        b2 = in.read();
                        b3 = in.read();
                        out.write(b2 & 0x0f);
                        out.write(b2 >> 4 | ((b3 & 0x0f) << 4));
                        out.write(b3 >> 4);
                    }
                } else {
                    for (; pxlen > 0; --pxlen) {
                        out.write(in.read());
                    }
                }
                out.close();
            }
            System.out.print('.');
        } finally {
            in.close();
        }
    }

public static void copyFile(File in, File out) throws IOException {
    FileChannel inChannel = new FileInputStream(in).getChannel();
    FileChannel outChannel = new FileOutputStream(out).getChannel();
    try {
        inChannel.transferTo(0, inChannel.size(), outChannel);
    } catch (IOException e) {
        throw e;
    } finally {
        if (inChannel != null) inChannel.close();
        if (outChannel != null) outChannel.close();
    }
}

public static void ize(String from_name, String toampath) throws IOException {
    File remote_log = new File(from_name);
    File to_file = new File(toampath);
    if (!remote_log.exists()) abort("FileCopy: no such source file: " + from_name);
    if (!remote_log.isFile()) abort("FileCopy: can't copy directory: " + from_name);
    if (!remote_log.canRead()) abort("FileCopy: source file is unreadable: " + from_name);
    if (to_file.isDirectory()) to_file = new File(to_file, remote_log.getName());
    if (to_file.exists()) {
        if (!to_file.canWrite()) abort("FileCopy: destination file is unwriteable: " + toampath);
    } else {
        String url = to_file.getParent();
        if (url == null) url = System.getProperty("user.dir");
        File block = new File(url);
        if (!block.exists()) abort("FileCopy: destination directory doesn't exist: " + url);
        if (block.isFile()) abort("FileCopy: destination is not a directory: " + url);
        if (!block.canWrite()) abort("FileCopy: destination directory is unwriteable: " + url);
    }
    FileInputStream name = null;
    FileOutputStream on = null;
    try {
        name = new FileInputStream(remote_log);
        on = new FileOutputStream(to_file);
        byte[] queue = new byte[4096];
        int bytes_current;
        while ((bytes_current = name.read(queue)) != -1) {
            on.write(queue, 0, bytes_current);
        }
    } finally {
        if (name != null) {
            try {
                name.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
        if (on != null) {
            try {
                on.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }
}

public static void copy(File from, File to) {
    boolean result;
    if (from.isDirectory()) {
        File[] subFiles = from.listFiles();
        for (int i = 0; i < subFiles.length; i++) {
            File newDir = new File(to, subFiles[i].getName());
            result = false;
            if (subFiles[i].isDirectory()) {
                if (newDir.exists()) result = true; else result = newDir.mkdirs();
            } else if (subFiles[i].isFile()) {
                try {
                    result = newDir.createNewFile();
                } catch (IOException e) {
                    log.error("unable to create new file: " + newDir, e);
                    result = false;
                }
            }
            if (result) copy(subFiles[i], newDir);
        }
    } else if (from.isFile()) {
        FileInputStream in = null;
        FileOutputStream out = null;
        try {
            in = new FileInputStream(from);
            out = new FileOutputStream(to);
            int fileLength = (int) from.length();
            char charBuff[] = new char[fileLength];
            int len;
            int oneChar;
            while ((oneChar = in.read()) != -1) {
                out.write(oneChar);
            }
        } catch (FileNotFoundException e) {
            log.error("File not found!", e);
        } catch (IOException e) {
            log.error("Unable to read from file!", e);
        } finally {
            try {
                if (in != null) in.close();
                if (out != null) out.close();
            } catch (IOException e1) {
                log.error("Error closing file reader/writer", e1);
            }
        }
    }
}

public DocumentSummary parseDocument(URL file) throws IOException, documentIOError {
    InputStream inputStream = null;
    try {
        inputStream = file.openStream();
        POIOLE2TextExtractor extractor = createExtractor(inputStream);
        SummaryInformation info = extractor.getSummaryInformation();
        DocumentSummary docSummary = new DocumentSummary();
        docSummary.users = DocSummaryPOIFSReaderListener.getAuthors(info);
        docSummary.outputFile = new StringReader(extractor.getText());
        docSummary.creationDate = info.getCreateDateTime();
        docSummary.keywords = new ArrayList();
        docSummary.keywords.add(info.getKeywords());
        docSummary.modifyDate = new Date(info.getEditTime());
        docSummary.content = info.getTitle();
        return docSummary;
    } catch (IOException e) {
        if (e.getMessage().startsWith("Unable to read entire header")) {
            throw new documentIOError("Couldn't process document", e);
        } else {
            throw e;
        }
    } finally {
        if (inputStream != null) {
            inputStream.close();
        }
    }
}

