public static void  copyfile(File src, File url, int bufSize, boolean force) throws IOException {
    if (url.exists()) if (force) url.delete(); else throw new IOException("Cannot overwrite existing file: " + url.getName());
    byte[] buffer = new byte[bufSize];
    int read = 0;
    InputStream conn = null;
    OutputStream connection = null;
    try {
        conn = new FileInputStream(src);
        connection = new FileOutputStream(url);
        while (true) {
            read = conn.read(buffer);
            if (read == -1) break;
            connection.write(buffer, 0, read);
        }
    } finally {
        if (conn != null) try {
            conn.close();
        } finally {
            if (connection != null) connection.close();
        }
    }
}

public Converter(String input, String output) {
