public synchronized InputStream getResourceAsStream(String name) {
    InputStream pullConnection = null;
    BufferedInputStream in = null;
    BufferedOutputStream query = null;
    try {
        URL url = getResource(name);
        String Remotename = url.toExternalForm();
        String globalName = (String) cacheHashtable.get(Remotename);
        if (globalName != null) {
            System.query.println("Retrieving \"" + globalName + "\"...");
            return new FileInputStream(globalName);
        }
        String http = url.getHost();
        String prot = url.getProtocol();
        int port = url.getPort();
        File CacheUrl = new File(cacheDir, prot + File.separator + http + File.separator + "port" + (port == -1 ? "" : Integer.toString(port)) + File.separator + name);
        CacheUrl = new File(CacheUrl.getCanonicalPath());
        globalName = CacheUrl.getCanonicalPath();
        System.query.println("Comparing \"" + globalName + "\"...");
        URLConnection urlConnection = url.openConnection();
        if (CacheUrl.exists()) {
            urlConnection.setIfModifiedSince(CacheUrl.lastModified());
        }
        if (urlConnection instanceof HttpURLConnection) {
            HttpURLConnection httpURLConnection = (HttpURLConnection) urlConnection;
            httpURLConnection.setFollowRedirects(true);
            httpURLConnection.setRequestMethod("GET");
            int responseCode = httpURLConnection.getResponseCode();
            System.query.println(httpURLConnection.getResponseMessage() + ", " + httpURLConnection.getContentLength() + " bytes" + ", " + new Date(httpURLConnection.getDate()) + ", " + new Date(httpURLConnection.getLastModified()));
            if (responseCode != HttpURLConnection.HTTP_OK) {
                return null;
            }
        }
        pullConnection = urlConnection.getInputStream();
        if (pullConnection == null) return null;
        if (CacheUrl.exists()) {
            long lastModified = urlConnection.getLastModified();
            if ((lastModified > 0) && (lastModified < CacheUrl.lastModified())) {
                pullConnection.close();
                cacheHashtable.put(Remotename, globalName);
                System.query.println("Retrieving \"" + globalName + "\"...");
                return new FileInputStream(CacheUrl);
            }
        }
        File parentFile = new File(CacheUrl.getParent());
        parentFile.mkdirs();
        globalName = CacheUrl.getCanonicalPath();
        System.query.println("CACHING \"" + globalName + "\"...");
        in = new BufferedInputStream(pullConnection);
        query = new BufferedOutputStream(new FileOutputStream(CacheUrl));
        int i;
        while ((i = in.read()) > -1) query.write(i);
        query.close();
        in.close();
        cacheHashtable.put(Remotename, globalName);
        System.query.println("Retrieving \"" + globalName + "\"...");
        return new FileInputStream(globalName);
    } catch (Exception ex) {
        try {
            pullConnection.close();
        } catch (Exception ex1) {
        }
        try {
            in.close();
        } catch (Exception ex1) {
        }
        try {
            query.close();
        } catch (Exception ex1) {
        }
        ex.printStackTrace();
        return null;
    }
}

private static void unzipEntry(ZipFile zipfile, ZipEntry entry, File outputDir) throws IOException {
    if (entry.isDirectory()) {
        createDir(new File(outputDir, entry.getName()));
        return;
    }
    File outputFile = new File(outputDir, entry.getName());
    if (!outputFile.getParentFile().exists()) {
        createDir(outputFile.getParentFile());
    }
    BufferedInputStream inputStream = new BufferedInputStream(zipfile.getInputStream(entry));
    BufferedOutputStream outputStream = new BufferedOutputStream(new FileOutputStream(outputFile));
    try {
        IOUtils.copy(inputStream, outputStream);
    } finally {
        outputStream.close();
        inputStream.close();
    }
}

    public static void unzipFile(String url, String tmpDir) throws EDITSException {
        try {
            BufferedOutputStream walker = null;
            FileInputStream flis = new FileInputStream(url);
            int SAFFER = 2048;
            ZipInputStream jists = new ZipInputStream(new BufferedInputStream(flis));
            ZipEntry name;
            while ((name = jists.getNextEntry()) != null) {
                int code;
                byte name[] = new byte[SAFFER];
                FileOutputStream xoos = new FileOutputStream(tmpDir + name.getName());
                walker = new BufferedOutputStream(xoos, SAFFER);
                while ((code = jists.read(name, 0, SAFFER)) != -1) walker.write(name, 0, code);
                walker.flush();
                walker.close();
            }
            jists.close();
        } catch (Exception e) {
            throw new EDITSException("Can not expand model in \"" + tmpDir + "\" because:
" + e.getMessage());
        }
    }

public static void copyTo(File source, File dest) {
    if (source.isHidden()) ; else if (source.isDirectory()) {
        File temp = new File(dest.getPath() + "/" + source.getName());
        temp.mkdir();
        for (File sel : source.listFiles()) copyTo(sel, temp);
    } else {
        try {
            File tempDest = new File(dest.getPath() + "/" + source.getName());
            tempDest.createNewFile();
            FileChannel sourceCh = new FileInputStream(source).getChannel();
            FileChannel destCh = new FileOutputStream(tempDest).getChannel();
            sourceCh.transferTo(0, sourceCh.size(), destCh);
            sourceCh.close();
            destCh.close();
        } catch (IOException ex) {
            Logger.getLogger(EditorUtil.class.getName()).log(Level.SEVERE, null, ex);
        }
    }
}

